import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-1203 What laws applicable to reciprocals; compliance with § 38.2-208.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1203.md","filePath":"vacode/38.2-1203.md"}'),o={name:"vacode/38.2-1203.md"},r=i('<h1 id="§-38-2-1203-what-laws-applicable-to-reciprocals-compliance-with-§-38-2-208" tabindex="-1">§ 38.2-1203 What laws applicable to reciprocals; compliance with § 38.2-208. <a class="header-anchor" href="#§-38-2-1203-what-laws-applicable-to-reciprocals-compliance-with-§-38-2-208" aria-label="Permalink to &quot;§ 38.2-1203 What laws applicable to reciprocals; compliance with § 38.2-208.&quot;">​</a></h1><p>A. Except as otherwise provided, all the provisions of this title relating to insurers generally, and those relating to insurers writing the same classes of insurance that reciprocals are permitted to write, are applicable to reciprocals.</p><p>B. A reciprocal shall be deemed to have complied with § <a href="http://law.lis.virginia.gov/vacode/38.2-208/" target="_blank" rel="noreferrer">38.2-208</a> if:</p><ol><li><p>It issues policies containing a contingent assessment liability as provided for in § <a href="http://law.lis.virginia.gov/vacode/38.2-1212/" target="_blank" rel="noreferrer">38.2-1212</a>; and</p></li><li><p>It has and maintains reinsurance in an amount that the Commission considers adequate to reasonably limit the reciprocal&#39;s aggregate losses to the lesser of:</p></li></ol><p>a. Ten percent of the surplus to policyholders of the reciprocal multiplied by the number of subscribers;</p><p>b. The surplus to policyholders of the reciprocal multiplied by three; or</p><p>c. Five million dollars.</p><p>Code 1950, § 38-543; 1952, c. 317, § 38.1-691; 1977, c. 58; 1986, c. 562.</p>',8),l=[r];function s(c,p,n,h,d,_){return a(),t("div",null,l)}const u=e(o,[["render",s]]);export{f as __pageData,u as default};
