import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-1306 Reports to be open to public inspection.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1306.md","filePath":"vacode/38.2-1306.md"}'),a={name:"vacode/38.2-1306.md"},n=i('<h1 id="§-38-2-1306-reports-to-be-open-to-public-inspection" tabindex="-1">§ 38.2-1306 Reports to be open to public inspection. <a class="header-anchor" href="#§-38-2-1306-reports-to-be-open-to-public-inspection" aria-label="Permalink to &quot;§ 38.2-1306 Reports to be open to public inspection.&quot;">​</a></h1><p>The Commission shall keep on file for at least three years all reports required by law and all special reports required by it to be filed by insurers. The Commission shall keep copies of the annual statement convention blanks and the quarterly financial statements filed with the Commission and, pursuant to subsection D of § <a href="http://law.lis.virginia.gov/vacode/38.2-1300/" target="_blank" rel="noreferrer">38.2-1300</a> and subsection B of § <a href="http://law.lis.virginia.gov/vacode/38.2-1301/" target="_blank" rel="noreferrer">38.2-1301</a> respectively, with the National Association of Insurance Commissioners (NAIC), available for inspection by interested persons at any reasonable time.</p><p>For companies not required to file with the NAIC, the Commission shall make available for inspection copies of such comparable financial statements of financial condition as those companies may be required to file routinely with the Commission pursuant to the provisions of this title. Except as provided otherwise by statute, or by order, rule or regulation promulgated by the Commission, no special report shall be open to public inspection.</p><p>Code 1950, § 38-124; 1952, c. 317, § 38.1-165; 1986, c. 562; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0308" target="_blank" rel="noreferrer">308</a>.</p>',4),r=[n];function s(l,p,c,h,d,b){return t(),o("div",null,r)}const u=e(a,[["render",s]]);export{m as __pageData,u as default};
