import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-1327 Time for hearing; order of Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1327.md","filePath":"vacode/38.2-1327.md"}'),a={name:"vacode/38.2-1327.md"},r=t('<h1 id="§-38-2-1327-time-for-hearing-order-of-commission" tabindex="-1">§ 38.2-1327 Time for hearing; order of Commission. <a class="header-anchor" href="#§-38-2-1327-time-for-hearing-order-of-commission" aria-label="Permalink to &quot;§ 38.2-1327 Time for hearing; order of Commission.&quot;">​</a></h1><p>A. Any hearing held pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-1326/" target="_blank" rel="noreferrer">38.2-1326</a> shall begin within 40 days of the date the application is filed with the Commission. In approving any application filed pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-1323/" target="_blank" rel="noreferrer">38.2-1323</a>, the Commission may include in its order any conditions, stipulations, or provisions that the Commission determines to be necessary to protect the interests of the policyholders of the insurer and the public.</p><p>B. The Commission may retain at the acquiring person&#39;s expense any attorneys, actuaries, accountants, and other experts not otherwise a part of the Commission&#39;s staff as may be reasonably necessary to assist the Commission in reviewing the proposed acquisition of control.</p><p>1977, c. 414, § 38.1-178.1:6; 1986, c. 562; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0309" target="_blank" rel="noreferrer">309</a>.</p>',4),n=[r];function s(c,h,m,l,p,d){return o(),i("div",null,n)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
