import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 38.2-1355 Examination authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1355.md","filePath":"vacode/38.2-1355.md"}'),i={name:"vacode/38.2-1355.md"},o=r('<h1 id="§-38-2-1355-examination-authority" tabindex="-1">§ 38.2-1355 Examination authority. <a class="header-anchor" href="#§-38-2-1355-examination-authority" aria-label="Permalink to &quot;§ 38.2-1355 Examination authority.&quot;">​</a></h1><p>A. A reinsurance intermediary shall be subject to examination by the Commission. The Commission shall have reasonable access to all books, bank accounts and records of the reinsurance intermediary in a form usable to the Commission.</p><p>B. A reinsurance intermediary manager may be examined, pursuant to Article 4 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-1317/" target="_blank" rel="noreferrer">38.2-1317</a> et seq.) of this chapter, as if it were the reinsurer. In addition, the reinsurance intermediary shall be subject to examination pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-1809/" target="_blank" rel="noreferrer">38.2-1809</a> if it or any of its officers, directors, agents, or employees is licensed as a producer under Chapter 18 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-1800/" target="_blank" rel="noreferrer">38.2-1800</a> et seq.) of this title.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',4),n=[o];function s(c,l,h,d,_,m){return a(),t("div",null,n)}const f=e(i,[["render",s]]);export{u as __pageData,f as default};
