import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-1374 Minimum reserves.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1374.md","filePath":"vacode/38.2-1374.md"}'),i={name:"vacode/38.2-1374.md"},n=t('<h1 id="§-38-2-1374-minimum-reserves" tabindex="-1">§ 38.2-1374 Minimum reserves. <a class="header-anchor" href="#§-38-2-1374-minimum-reserves" aria-label="Permalink to &quot;§ 38.2-1374 Minimum reserves.&quot;">​</a></h1><p>A. In no event shall an insurer&#39;s aggregate reserves for all life insurance policies, excluding disability and accidental death benefits, be less than the aggregate reserves calculated in accordance with the methods set forth in §§ <a href="http://law.lis.virginia.gov/vacode/38.2-1372/" target="_blank" rel="noreferrer">38.2-1372</a>, <a href="http://law.lis.virginia.gov/vacode/38.2-1373/" target="_blank" rel="noreferrer">38.2-1373</a>, <a href="http://law.lis.virginia.gov/vacode/38.2-1376/" target="_blank" rel="noreferrer">38.2-1376</a>, and <a href="http://law.lis.virginia.gov/vacode/38.2-1377/" target="_blank" rel="noreferrer">38.2-1377</a> and the mortality table or tables and rate or rates of interest used in calculating nonforfeiture benefits for those policies.</p><p>B. In no event shall the aggregate reserves for all policies, contracts, and benefits be less than the aggregate reserves determined by the appointed actuary to be necessary to render the opinion required by § <a href="http://law.lis.virginia.gov/vacode/38.2-1367/" target="_blank" rel="noreferrer">38.2-1367</a>.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0571" target="_blank" rel="noreferrer">571</a>.</p>',4),s=[n];function o(l,c,d,h,g,_){return r(),a("div",null,s)}const p=e(i,[["render",o]]);export{v as __pageData,p as default};
