import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-1421 Business entity obligations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1421.md","filePath":"vacode/38.2-1421.md"}'),s={name:"vacode/38.2-1421.md"},n=a('<h1 id="§-38-2-1421-business-entity-obligations" tabindex="-1">§ 38.2-1421 Business entity obligations. <a class="header-anchor" href="#§-38-2-1421-business-entity-obligations" aria-label="Permalink to &quot;§ 38.2-1421 Business entity obligations.&quot;">​</a></h1><p>A. High grade. A domestic insurer may invest in any high grade obligations issued, assumed or guaranteed by any solvent business entity that is not in default as to principal or interest on the date of investment and which is created or existing under the laws of the United States or any state.</p><p>B. Medium grade. A domestic issuer may invest in medium grade obligations issued, assumed or guaranteed by any solvent business entity that is not in default as to principal or interest on the date of investment and which is created or existing under the laws of the United States or any state.</p><p>C. Lower grade. A domestic insurer may invest in lower grade obligations rated 4 by the Securities Valuation Office of the National Association of Insurance Commissioners or, if not rated by the Securities Valuation Office, rated in an equivalent grade by a national rating agency recognized by the Commission that are issued, assumed or guaranteed by any solvent business entity that is not in default as to principal or interest on the date of investment and which is created or existing under the laws of the United States or any state.</p><p>D. As used in this section, &quot;business entity obligations&quot; shall not include any mortgage pass-through securities described in § <a href="http://law.lis.virginia.gov/vacode/38.2-1437.1/" target="_blank" rel="noreferrer">38.2-1437.1</a>.</p><p>1983, c. 457, § 38.1-217.24; 1986, c. 562; 1992, c. 588; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0414" target="_blank" rel="noreferrer">414</a>.</p>',6),o=[n];function r(d,c,l,u,h,g){return t(),i("div",null,o)}const f=e(s,[["render",r]]);export{_ as __pageData,f as default};
