import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-1423 Preferred stocks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1423.md","filePath":"vacode/38.2-1423.md"}'),i={name:"vacode/38.2-1423.md"},o=a('<h1 id="§-38-2-1423-preferred-stocks" tabindex="-1">§ 38.2-1423 Preferred stocks. <a class="header-anchor" href="#§-38-2-1423-preferred-stocks" aria-label="Permalink to &quot;§ 38.2-1423 Preferred stocks.&quot;">​</a></h1><p>A domestic insurer may invest in preferred stocks of any company incorporated under the laws of the United States or any state if:</p><ol><li>a. The preferred stock under consideration is not in arrears as to dividends if cumulative, or</li></ol><p>b. Full dividends on the preferred stock under consideration have been paid in the last three years, or since issue if issued less than three years before the date of investment, if noncumulative;</p><ol start="2"><li><p>Required sinking fund payments are on a current basis; and</p></li><li><p>The preferred stock is rated highest quality, high quality, or medium quality by the Securities Valuation Office of the National Association of Insurance Commissioners, or if not rated by the Securities Valuation Office, is rated in an equivalent grade by a national rating agency recognized by the Commission.</p></li></ol><p>1983, c. 457, § 38.1-217.26; 1986, c. 562; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0414" target="_blank" rel="noreferrer">414</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0093" target="_blank" rel="noreferrer">93</a>.</p>',6),s=[o];function n(c,d,l,f,p,h){return r(),t("div",null,s)}const m=e(i,[["render",n]]);export{_ as __pageData,m as default};
