import{_ as e,o as i,c as t,O as s}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-1503 Grounds for delinquency proceedings commenced by Commission against domestic insurer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1503.md","filePath":"vacode/38.2-1503.md"}'),o={name:"vacode/38.2-1503.md"},r=s('<h1 id="§-38-2-1503-grounds-for-delinquency-proceedings-commenced-by-commission-against-domestic-insurer" tabindex="-1">§ 38.2-1503 Grounds for delinquency proceedings commenced by Commission against domestic insurer. <a class="header-anchor" href="#§-38-2-1503-grounds-for-delinquency-proceedings-commenced-by-commission-against-domestic-insurer" aria-label="Permalink to &quot;§ 38.2-1503 Grounds for delinquency proceedings commenced by Commission against domestic insurer.&quot;">​</a></h1><p>Delinquency proceedings may be commenced by the Commission against any domestic insurer whenever the insurer:</p><ol><li><p>Has been determined to be insolvent by the Commission;</p></li><li><p>Has refused to submit its books, papers, accounts, records, or affairs to the reasonable inspection of the Commission or its representative;</p></li><li><p>Has refused or failed to comply with any order of the Commission to make good within the time prescribed by law (i) any impairment of its minimum capital and surplus if the insurer is a stock insurer, (ii) any impairment of its minimum surplus if the insurer is other than a stock insurer, or (iii) membership requirements as set forth in § <a href="http://law.lis.virginia.gov/vacode/38.2-2515/" target="_blank" rel="noreferrer">38.2-2515</a> if the insurer is a mutual assessment property and casualty insurer and has had its license revoked;</p></li><li><p>Has transferred or attempted to transfer substantially its entire property, or has entered into any transaction which merges substantially its entire property or business, into the property or business of any other company without prior written approval of the Commission;</p></li><li><p>Has removed, attempted to remove, or is about to remove from this Commonwealth any material part of its property or business necessary for the continued conduct of its business if it endangers the interests of its policyholders, stockholders or members;</p></li><li><p>Has reinsured all or substantially all of its risks without prior written approval of the Commission;</p></li><li><p>Is found, after an examination, to be in a condition where any further transaction of business will be hazardous to its policyholders, creditors, members, subscribers, stockholders, or to the public;</p></li><li><p>Has willfully violated its charter or any law of this Commonwealth;</p></li><li><p>Has an officer, director or manager who has refused to be examined under oath concerning its affairs;</p></li><li><p>Has had any material part of its entire property sequestered in any other state or country;</p></li><li><p>Has not organized or completed its organization and obtained a license to transact the business of insurance in this Commonwealth within the period of time set by law; or</p></li><li><p>Has failed to pay a final judgment rendered against it in any state upon any insurance contract issued or assumed by it (i) within sixty days after the judgment has become final, (ii) within sixty days after time for taking an appeal has expired, or (iii) within sixty days after dismissal of an appeal before final determination, whichever date is the latest.</p></li></ol><p>Code 1950, § 38-138; 1952, c. 317, § 38.1-129; 1986, c. 562.</p>',4),a=[r];function n(d,l,m,c,p,u){return i(),t("div",null,a)}const y=e(o,[["render",n]]);export{f as __pageData,y as default};
