import{_ as t,o as i,c as o,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-1518 Rehabilitation or mutualization of companies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1518.md","filePath":"vacode/38.2-1518.md"}'),r={name:"vacode/38.2-1518.md"},n=e("h1",{id:"§-38-2-1518-rehabilitation-or-mutualization-of-companies",tabindex:"-1"},[a("§ 38.2-1518 Rehabilitation or mutualization of companies. "),e("a",{class:"header-anchor",href:"#§-38-2-1518-rehabilitation-or-mutualization-of-companies","aria-label":'Permalink to "§ 38.2-1518 Rehabilitation or mutualization of companies."'},"​")],-1),s=e("p",null,"If at any time the Commission acting as the receiver finds that it is in the best interests of the policyholders and creditors of a delinquent insurer that it be rehabilitated or mutualized, the Commission shall prepare a plan of rehabilitation or mutualization. If at any time a receiver, other than the Commission, of a delinquent insurer reports to the court that it is in the best interests of the policyholders and creditors of the insurer that it be rehabilitated or mutualized, the receiver shall submit a plan of rehabilitation or mutualization to the court for its approval. The plan may include a provision imposing liens upon the net equities of policyholders of the insurer, and in the case of life insurers, a provision imposing a moratorium upon the loan or cash surrender values of the policies for whatever period of time is necessary. A hearing on the plan shall be held and notice of the hearing given in a manner prescribed by either the Commission or the court. After the hearing, the plan may be approved, disapproved, or modified by the Commission or the court.",-1),h=e("p",null,"Code 1950, § 38-139; 1952, c. 317, § 38.1-142; 1986, c. 562.",-1),l=[n,s,h];function c(d,m,p,u,f,b){return i(),o("div",null,l)}const y=t(r,[["render",c]]);export{v as __pageData,y as default};
