import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 38.2-1620 Financing the safety fund, maximum amount, distribution of excess.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1620.md","filePath":"vacode/38.2-1620.md"}'),i={name:"vacode/38.2-1620.md"},r=n('<h1 id="§-38-2-1620-financing-the-safety-fund-maximum-amount-distribution-of-excess" tabindex="-1">§ 38.2-1620 Financing the safety fund, maximum amount, distribution of excess. <a class="header-anchor" href="#§-38-2-1620-financing-the-safety-fund-maximum-amount-distribution-of-excess" aria-label="Permalink to &quot;§ 38.2-1620 Financing the safety fund, maximum amount, distribution of excess.&quot;">​</a></h1><p>A. The safety fund, at the discretion of the Commission, shall receive penalty payments levied against member insurers made pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/38.2-225/" target="_blank" rel="noreferrer">38.2-225</a> or any other payments approved by the Commission. Such payments shall include funds borrowed under the provisions of subdivision B 7 of § <a href="http://law.lis.virginia.gov/vacode/38.2-1606/" target="_blank" rel="noreferrer">38.2-1606</a> in the event of a natural disaster in order to provide for the prompt payment of covered claims and expenses related thereto.</p><p>B. The Commission may approve the payment of funds to the Association provided the balance in the safety fund account does not exceed two percent of the total of all member insurers&#39; net direct written premiums for classes of insurance covered by the accounts specified in § <a href="http://law.lis.virginia.gov/vacode/38.2-1604/" target="_blank" rel="noreferrer">38.2-1604</a>.</p><p>C. Except as provided in subsection D of this section, investment income earned on assets held in the safety fund shall be credited to the safety fund.</p><p>D. In the event the safety fund balance exceeds three percent of the net written premium for all classes of insurance covered by the accounts specified in § <a href="http://law.lis.virginia.gov/vacode/38.2-1604/" target="_blank" rel="noreferrer">38.2-1604</a>, at the discretion of the Commission the difference shall be paid to the state treasury to the credit of the Literary Fund or shall be subject to subsection F of § <a href="http://law.lis.virginia.gov/vacode/38.2-1622/" target="_blank" rel="noreferrer">38.2-1622</a>.</p><p>E. In the event the fund is dissolved, remaining assets in the safety fund will be distributed to the state treasury to the credit of the Literary Fund.</p><p>1986, c. 562; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0230" target="_blank" rel="noreferrer">230</a>.</p>',7),o=[r];function s(d,c,f,h,l,u){return t(),a("div",null,o)}const v=e(i,[["render",s]]);export{p as __pageData,v as default};
