import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-1812.2 Administrative charges in excess of premium prohibited; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1812.2.md","filePath":"vacode/38.2-1812.2.md"}'),t={name:"vacode/38.2-1812.2.md"},n=a('<h1 id="§-38-2-1812-2-administrative-charges-in-excess-of-premium-prohibited-exceptions" tabindex="-1">§ 38.2-1812.2 Administrative charges in excess of premium prohibited; exceptions. <a class="header-anchor" href="#§-38-2-1812-2-administrative-charges-in-excess-of-premium-prohibited-exceptions" aria-label="Permalink to &quot;§ 38.2-1812.2 Administrative charges in excess of premium prohibited; exceptions.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-310/" target="_blank" rel="noreferrer">38.2-310</a> and Article 4 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-1837/" target="_blank" rel="noreferrer">38.2-1837</a> et seq.) of this chapter, no agent shall charge, or demand or receive from, an applicant for insurance or a policyholder any consideration in return for rendering services associated with a contract of insurance, when the consideration is in addition to the premium for such contract, unless:</p><ol><li><p>The applicant or policyholder consents in writing before any services are rendered. Consent shall be provided on a form that includes the applicant&#39;s or policyholder&#39;s signature, the duration of services and amount of fees to be charged, the services for which the fees are charged, and a statement that the agent is entitled to receive a commission from the insurer for selling, soliciting, or negotiating the insurance; and</p></li><li><p>A schedule of fees and documentation for services rendered is maintained in the agent&#39;s office and is made available to applicants or policyholders upon request.</p></li></ol><p>B. This section shall not apply to charges for services described in subsection C of § <a href="http://law.lis.virginia.gov/vacode/38.2-4608/" target="_blank" rel="noreferrer">38.2-4608</a> when provided by title insurance agents.</p><p>C. This section shall apply to new and renewal policies issued or renewed on or after July 1, 1999.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',6),o=[n];function s(c,l,d,h,p,f){return i(),r("div",null,o)}const v=e(t,[["render",s]]);export{m as __pageData,v as default};
