import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 38.2-1815 License required of resident life and annuities agent.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1815.md","filePath":"vacode/38.2-1815.md"}'),t={name:"vacode/38.2-1815.md"},r=n('<h1 id="§-38-2-1815-license-required-of-resident-life-and-annuities-agent" tabindex="-1">§ 38.2-1815 License required of resident life and annuities agent. <a class="header-anchor" href="#§-38-2-1815-license-required-of-resident-life-and-annuities-agent" aria-label="Permalink to &quot;§ 38.2-1815 License required of resident life and annuities agent.&quot;">​</a></h1><p>A. No individual who is a resident of the Commonwealth shall obtain a license as a life and annuities agent from the Commission unless the individual has passed an examination in a form and manner prescribed by the Commission. The Commission annually shall review whether the examination&#39;s pass rate is consistent with the 2013 NAIC State Licensing Handbook, or any successor publication adopted by the NAIC.</p><p>B. An individual may obtain a license as a limited lines credit insurance agent, a limited lines life and health agent, a motor vehicle rental contract insurance agent, or any other type of license of restricted authority that the Commission may deem it necessary to recognize for the purposes of complying with § <a href="/vacode/38.2-1836/">38.2-1836</a> without taking such examination.</p><p>C. No individual who is a resident of the Commonwealth shall obtain a license as a variable contract agent unless the individual currently holds a life and annuities license, and no individual, whether resident or nonresident, shall obtain a license as a variable contract agent unless the individual has passed the Financial Industry Regulatory Authority examination or examinations prescribed by the Commission or such other examination prescribed by the Commission.</p><p>1979, c. 513, § 38.1-327.24; 1982, c. 223; 1983, cc. 160, 185; 1985, c. 616; 1986, c. 562; 1987, c. 521; 1993, c. 695; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0047" target="_blank" rel="noreferrer">47</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0086" target="_blank" rel="noreferrer">86</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0281" target="_blank" rel="noreferrer">281</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0413" target="_blank" rel="noreferrer">413</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0223" target="_blank" rel="noreferrer">223</a>.</p>',5),s=[r];function o(l,c,d,h,f,u){return i(),a("div",null,s)}const m=e(t,[["render",o]]);export{p as __pageData,m as default};
