import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-1839 Contract required; placement of insurance for public bodies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1839.md","filePath":"vacode/38.2-1839.md"}'),r={name:"vacode/38.2-1839.md"},a=n('<h1 id="§-38-2-1839-contract-required-placement-of-insurance-for-public-bodies" tabindex="-1">§ 38.2-1839 Contract required; placement of insurance for public bodies. <a class="header-anchor" href="#§-38-2-1839-contract-required-placement-of-insurance-for-public-bodies" aria-label="Permalink to &quot;§ 38.2-1839 Contract required; placement of insurance for public bodies.&quot;">​</a></h1><p>A. A licensed insurance consultant that does not sell, solicit or negotiate insurance as part of his services shall enter into a written contract with his client prior to any act as a consultant in this Commonwealth. A licensed insurance consultant that does sell, solicit or negotiate insurance in this Commonwealth as part of his services shall enter into a written contract with his client prior to the purchase of any insurance by that client. The contract shall include, without limitation, the amount and basis of any consulting fee and the duration of employment. If the insurance consultant may also receive commissions, incentives, bonuses, overrides, or any other form of remuneration either directly or indirectly as a result of his services for selling, soliciting, or negotiating insurance as a part of his services in addition to a consulting fee, unless otherwise prohibited, such information shall be disclosed in the contract.</p><p>B. No insurance consultant may provide or offer to provide, directly or indirectly, insurance products to a public body while concurrently and on its behalf (i) evaluating proposals from other insurance agents and (ii) recommending the placement of insurance.</p><p>1985, c. 3, § 38.1-327.64; 1986, c. 562; 1987, cc. 521, 678; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0989" target="_blank" rel="noreferrer">989</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0621" target="_blank" rel="noreferrer">621</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0449" target="_blank" rel="noreferrer">449</a>.</p>',4),o=[a];function c(s,l,u,h,d,f){return t(),i("div",null,o)}const _=e(r,[["render",c]]);export{m as __pageData,_ as default};
