import{_ as e,o as t,c as n,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 38.2-1842 Requirement to report to Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1842.md","filePath":"vacode/38.2-1842.md"}'),i={name:"vacode/38.2-1842.md"},o=a('<h1 id="§-38-2-1842-requirement-to-report-to-commission" tabindex="-1">§ 38.2-1842 Requirement to report to Commission. <a class="header-anchor" href="#§-38-2-1842-requirement-to-report-to-commission" aria-label="Permalink to &quot;§ 38.2-1842 Requirement to report to Commission.&quot;">​</a></h1><p>A. Each licensed insurance consultant shall report within 30 calendar days to the Commission any change in his residence address, email address, or name.</p><p>B. In addition to the requirements of §§ <a href="/vacode/59.1-69/">59.1-69</a> and <a href="/vacode/59.1-70/">59.1-70</a>, any individual or business entity licensed as an insurance consultant in the Commonwealth and operating under an assumed or fictitious name shall notify the Commission, at the earlier of the time the application for an insurance consultant license is filed or within 30 calendar days from the date the assumed or fictitious name is adopted, setting forth the name under which the insurance consultant intends to operate in Virginia. The Commission shall also be notified within 30 calendar days from the date of cessation of the use of such assumed or fictitious name.</p><p>C. Each licensed insurance consultant convicted of a felony shall report within 30 calendar days to the Commission the facts and circumstances regarding the criminal conviction.</p><p>D. Each licensed insurance consultant shall report to the Commission within 30 calendar days of the final disposition of the matter any administrative action taken against him in another jurisdiction or by another governmental agency in the Commonwealth. Such report shall include a copy of the order, consent to order or other relevant legal documents.</p><p>E. The license authority of any licensed resident insurance consultant shall terminate immediately when such insurance consultant has moved his residence from the Commonwealth, whether or not the Commission has been notified of such move.</p><p>1985, c. 3, § 38.1-327.67; 1986, c. 562; 1987, cc. 521, 678; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0059" target="_blank" rel="noreferrer">59</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0675" target="_blank" rel="noreferrer">675</a>.</p>',7),r=[o];function s(c,h,l,d,m,u){return t(),n("div",null,r)}const _=e(i,[["render",s]]);export{p as __pageData,_ as default};
