import{_ as e,o as n,c as i,O as o}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 38.2-1843 Grounds for placing on probation, refusal to issue or renew, revocation or suspension of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1843.md","filePath":"vacode/38.2-1843.md"}'),r={name:"vacode/38.2-1843.md"},a=o('<h1 id="§-38-2-1843-grounds-for-placing-on-probation-refusal-to-issue-or-renew-revocation-or-suspension-of-license" tabindex="-1">§ 38.2-1843 Grounds for placing on probation, refusal to issue or renew, revocation or suspension of license. <a class="header-anchor" href="#§-38-2-1843-grounds-for-placing-on-probation-refusal-to-issue-or-renew-revocation-or-suspension-of-license" aria-label="Permalink to &quot;§ 38.2-1843 Grounds for placing on probation, refusal to issue or renew, revocation or suspension of license.&quot;">​</a></h1><p>The Commission may, in addition to or in lieu of a penalty imposed under § <a href="http://law.lis.virginia.gov/vacode/38.2-218/" target="_blank" rel="noreferrer">38.2-218</a>, place on probation, suspend, revoke or refuse to issue or renew any person&#39;s license for any one or more of the following causes:</p><ol><li><p>Providing materially incorrect, misleading, incomplete or untrue information in the license application or any other document filed with the Commission;</p></li><li><p>Violating any insurance laws, or violating any regulation, subpoena or order of the Commission or of another state&#39;s insurance regulatory authority;</p></li><li><p>Obtaining or attempting to obtain a license through misrepresentation or fraud;</p></li><li><p>Improperly withholding, misappropriating or converting any moneys or properties received in the course of doing insurance consulting business;</p></li><li><p>Engaging in twisting or any form thereof, where &quot;twisting&quot; means inducing an insured to terminate an existing policy and purchase a new policy through misrepresentation;</p></li><li><p>Engaging in the practice of rebating;</p></li><li><p>Intentionally misrepresenting the terms of an actual or proposed insurance contract or application for insurance;</p></li><li><p>Having admitted or been found to have committed any insurance unfair trade practice or fraud, including reducing the fee or compensation provided for in § <a href="http://law.lis.virginia.gov/vacode/38.2-1837/" target="_blank" rel="noreferrer">38.2-1837</a> for the purpose of inducing a client or potential client to purchase a policy;</p></li><li><p>Having been convicted of a felony;</p></li><li><p>Using fraudulent, coercive, or dishonest practices, or demonstrating incompetence, or untrustworthiness in the conduct of business in this Commonwealth or elsewhere, or demonstrating financial irresponsibility in the handling of applicant, policyholder, agency, or insurance company funds;</p></li><li><p>Having an insurance producer or consultant license, or its equivalent, denied, suspended or revoked in any other state, province, district or territory;</p></li><li><p>Forging another&#39;s name to an application for insurance or to any document related to an insurance transaction;</p></li><li><p>Improperly using notes or any other reference material to complete an examination for an insurance license;</p></li><li><p>Knowingly accepting insurance business from an individual who is not licensed;</p></li><li><p>Failing to comply with an administrative or court order imposing a child support obligation; or</p></li><li><p>Failing to pay state income tax or comply with any administrative or court order directing payment of state income tax.</p></li></ol><p>1985, c. 3, § 38.1-327.68; 1986, c. 562; 1987, cc. 521, 678; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0010" target="_blank" rel="noreferrer">10</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',4),t=[a];function s(l,c,p,u,d,g){return n(),i("div",null,t)}const m=e(r,[["render",s]]);export{h as __pageData,m as default};
