import{_ as e,o as i,c as o,O as n}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 38.2-1845.10 Grounds for placing on probation, refusal to issue or renew, revocation, or suspension of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1845.10.md","filePath":"vacode/38.2-1845.10.md"}'),r={name:"vacode/38.2-1845.10.md"},a=n('<h1 id="§-38-2-1845-10-grounds-for-placing-on-probation-refusal-to-issue-or-renew-revocation-or-suspension-of-license" tabindex="-1">§ 38.2-1845.10 Grounds for placing on probation, refusal to issue or renew, revocation, or suspension of license. <a class="header-anchor" href="#§-38-2-1845-10-grounds-for-placing-on-probation-refusal-to-issue-or-renew-revocation-or-suspension-of-license" aria-label="Permalink to &quot;§ 38.2-1845.10 Grounds for placing on probation, refusal to issue or renew, revocation, or suspension of license.&quot;">​</a></h1><p>The Commission may, in addition to or in lieu of a penalty imposed under § <a href="http://law.lis.virginia.gov/vacode/38.2-218/" target="_blank" rel="noreferrer">38.2-218</a>, place on probation, suspend, revoke, or refuse to issue or renew any person&#39;s license for any one or more of the following causes:</p><ol><li><p>Providing materially incorrect, misleading, incomplete, or untrue information in the license application or any other document filed with the Commission;</p></li><li><p>Violating any insurance laws or violating any regulation, subpoena, or order of the Commission or of another state&#39;s insurance regulatory authority;</p></li><li><p>Obtaining or attempting to obtain a license through misrepresentation or fraud;</p></li><li><p>Improperly withholding, misappropriating, or converting any moneys or properties received in the course of doing business as a public adjuster;</p></li><li><p>Having been convicted of a felony, a crime of moral turpitude, or any criminal offense involving dishonesty or a breach of trust;</p></li><li><p>Having admitted or been found to have committed any insurance unfair trade practice, as set forth in Chapter 5 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-500/" target="_blank" rel="noreferrer">38.2-500</a> et seq.), or fraud;</p></li><li><p>Using fraudulent, coercive, or dishonest practices or demonstrating incompetence or untrustworthiness in the conduct of business in the Commonwealth or elsewhere, or demonstrating financial irresponsibility in the handling of policyholder, agency, or insurance company funds;</p></li><li><p>Having public adjuster license, or its equivalent, denied, suspended, or revoked in any other state, province, district, or territory;</p></li><li><p>Intentionally misrepresenting the terms of an insurance contract;</p></li><li><p>Knowingly accepting public adjusting business from an individual who unlawfully solicited business and who is not licensed but who is required to be licensed under this article;</p></li><li><p>Paying or sharing a commission, fee, or other valuable consideration to a person who is required to be licensed under this article and is not so licensed;</p></li><li><p>Forging another&#39;s name to any document related to an insurance transaction;</p></li><li><p>Improperly using notes or any other reference material to complete an examination for a public adjuster license;</p></li><li><p>Failing to comply with an administrative or court order imposing a child support obligation;</p></li><li><p>Failing to pay Virginia income tax or comply with any administrative or court order directing payment of state income tax; or</p></li><li><p>Failing to report to the Commission as required by § <a href="http://law.lis.virginia.gov/vacode/38.2-1845.17/" target="_blank" rel="noreferrer">38.2-1845.17</a>.</p></li></ol><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>.</p>',4),t=[a];function s(l,c,p,u,d,f){return i(),o("div",null,t)}const m=e(r,[["render",s]]);export{h as __pageData,m as default};
