import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-1845.17 Requirement to report to Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1845.17.md","filePath":"vacode/38.2-1845.17.md"}'),a={name:"vacode/38.2-1845.17.md"},n=i('<h1 id="§-38-2-1845-17-requirement-to-report-to-commission" tabindex="-1">§ 38.2-1845.17 Requirement to report to Commission. <a class="header-anchor" href="#§-38-2-1845-17-requirement-to-report-to-commission" aria-label="Permalink to &quot;§ 38.2-1845.17 Requirement to report to Commission.&quot;">​</a></h1><p>A. Each licensed public adjuster shall report within 30 calendar days to the Commission any change in his residence address, email address, or name.</p><p>B. In addition to the requirements of §§ <a href="/vacode/59.1-69/">59.1-69</a> and <a href="/vacode/59.1-70/">59.1-70</a>, any individual or business entity licensed as a public adjuster in the Commonwealth and operating under an assumed or fictitious name shall provide notice to the Commission, at the earlier of the time the application for a public adjuster license is filed or within 30 calendar days from the date the assumed or fictitious name is adopted, setting forth the name under which the public adjuster intends to operate in the Commonwealth. The Commission shall also be notified within 30 calendar days from the date of cessation of the use of such assumed or fictitious name.</p><p>C. Each licensed public adjuster shall report to the Commission any conviction involving a felony, a crime of moral turpitude, or any criminal offense involving dishonesty or breach of trust in another jurisdiction or in the Commonwealth within 30 calendar days of the final disposition of the matter. This report shall include a copy of the order and other relevant legal documents.</p><p>D. Each licensed public adjuster shall report to the Commission within 30 calendar days of the final disposition of the matter of any administrative action taken against him in another jurisdiction or by another governmental agency in the Commonwealth. Such report shall include a copy of the order, consent order, or other relevant legal documents.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0675" target="_blank" rel="noreferrer">675</a>.</p>',6),r=[n];function s(l,c,d,h,m,p){return t(),o("div",null,r)}const _=e(a,[["render",s]]);export{f as __pageData,_ as default};
