import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 38.2-1845.18 Information security program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1845.18.md","filePath":"vacode/38.2-1845.18.md"}'),r={name:"vacode/38.2-1845.18.md"},o=i('<h1 id="§-38-2-1845-18-information-security-program" tabindex="-1">§ 38.2-1845.18 Information security program. <a class="header-anchor" href="#§-38-2-1845-18-information-security-program" aria-label="Permalink to &quot;§ 38.2-1845.18 Information security program.&quot;">​</a></h1><p>A. Each public adjuster shall implement a comprehensive written information security program that includes administrative, technical, and physical safeguards for the protection of policyholder information. The administrative, technical, and physical safeguards included in the information security program shall be appropriate to the size and complexity of the public adjuster&#39;s business and the nature and scope of its activities.</p><p>B. The information security program shall be designed to:</p><ol><li><p>Ensure the security and confidentiality of policyholder information;</p></li><li><p>Protect against any anticipated threats or hazards to the security or integrity of the information; and</p></li><li><p>Protect against unauthorized access to or use of the information that could result in substantial harm or inconvenience to any policyholder.</p></li></ol><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>.</p>',5),n=[o];function s(c,l,p,d,h,f){return t(),a("div",null,n)}const _=e(r,[["render",s]]);export{u as __pageData,_ as default};
