import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 38.2-1845.22 Power of Commission to investigate affairs of persons engaged in the business of public adjusting; penalties for refusal to permit investigation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1845.22.md","filePath":"vacode/38.2-1845.22.md"}'),n={name:"vacode/38.2-1845.22.md"},s=o('<h1 id="§-38-2-1845-22-power-of-commission-to-investigate-affairs-of-persons-engaged-in-the-business-of-public-adjusting-penalties-for-refusal-to-permit-investigation" tabindex="-1">§ 38.2-1845.22 Power of Commission to investigate affairs of persons engaged in the business of public adjusting; penalties for refusal to permit investigation. <a class="header-anchor" href="#§-38-2-1845-22-power-of-commission-to-investigate-affairs-of-persons-engaged-in-the-business-of-public-adjusting-penalties-for-refusal-to-permit-investigation" aria-label="Permalink to &quot;§ 38.2-1845.22 Power of Commission to investigate affairs of persons engaged in the business of public adjusting; penalties for refusal to permit investigation.&quot;">​</a></h1><p>The Commission shall have power to examine and investigate the business affairs of any person engaged or alleged to be engaged in the business of public adjusting in the Commonwealth to determine whether the person has engaged or is engaging in any violation of this title. The Commission shall have the right to examine all records relating to the business of public adjusting by any such person in the Commonwealth to determine whether the person is now or has been violating any of the provisions of this title. Any licensee under this article or any person purporting to be a licensee under this article or any person whose actions have led any person to believe that he is a licensee under this article who refuses to permit the Commission or any of its employees or agents, including employees of the Bureau of Insurance, to make an examination or who fails or refuses to comply with the provisions of this section, may, after notice and an opportunity to be heard, be subject to any of the penalties relating to licensees under this article, as provided in this title, including the termination, denial, suspension, or revocation of his license.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0675" target="_blank" rel="noreferrer">675</a>.</p>',3),a=[s];function r(l,f,h,p,g,c){return i(),t("div",null,a)}const m=e(n,[["render",r]]);export{d as __pageData,m as default};
