import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-1845.6 Individual moving into the Commonwealth from another state or Canadian province.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1845.6.md","filePath":"vacode/38.2-1845.6.md"}'),t={name:"vacode/38.2-1845.6.md"},o=n('<h1 id="§-38-2-1845-6-individual-moving-into-the-commonwealth-from-another-state-or-canadian-province" tabindex="-1">§ 38.2-1845.6 Individual moving into the Commonwealth from another state or Canadian province. <a class="header-anchor" href="#§-38-2-1845-6-individual-moving-into-the-commonwealth-from-another-state-or-canadian-province" aria-label="Permalink to &quot;§ 38.2-1845.6 Individual moving into the Commonwealth from another state or Canadian province.&quot;">​</a></h1><p>A. An individual holding a nonresident Virginia public adjuster license who has moved into the Commonwealth from another state or a province of Canada shall submit the application and pay the license processing fee required by and in accordance with the requirements set forth in § <a href="http://law.lis.virginia.gov/vacode/38.2-1845.2/" target="_blank" rel="noreferrer">38.2-1845.2</a>. A public adjuster with an active nonresident Virginia public adjuster license may continue to operate under his nonresident license for up to 90 calendar days while applying for a resident Virginia public adjuster&#39;s license. If a nonresident public adjuster fails to obtain such resident license by the end of the 90-calendar-day period, the equivalent nonresident license shall terminate.</p><p>B. An individual licensed as a public adjuster in another state or province of Canada, but not holding a nonresident Virginia public adjuster license, who moves to the Commonwealth shall submit the application to become a resident public adjuster and shall pay the license processing fee set forth in § <a href="http://law.lis.virginia.gov/vacode/38.2-1845.2/" target="_blank" rel="noreferrer">38.2-1845.2</a> within 90 calendar days of establishing residency in the Commonwealth as provided by subsection B of § <a href="http://law.lis.virginia.gov/vacode/38.2-1800.1/" target="_blank" rel="noreferrer">38.2-1800.1</a>. No prelicensing examination shall be required of that individual to obtain a public adjuster license. After establishing legal residence in the Commonwealth and prior to obtaining a license as a resident public adjuster, the individual shall be prohibited from conducting the business of public adjusting in the Commonwealth. An individual who fails to submit the application and license processing fee within 90 calendar days of establishing legal residence in the Commonwealth shall be required to satisfy all resident public adjuster prelicensing requirements required by this article.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>.</p>',4),r=[o];function s(l,d,c,h,p,u){return i(),a("div",null,r)}const g=e(t,[["render",s]]);export{f as __pageData,g as default};
