import{_ as e,o as i,c as o,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-1857.5 Requirement to report to Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1857.5.md","filePath":"vacode/38.2-1857.5.md"}'),n={name:"vacode/38.2-1857.5.md"},r=t('<h1 id="§-38-2-1857-5-requirement-to-report-to-commission" tabindex="-1">§ 38.2-1857.5 Requirement to report to Commission. <a class="header-anchor" href="#§-38-2-1857-5-requirement-to-report-to-commission" aria-label="Permalink to &quot;§ 38.2-1857.5 Requirement to report to Commission.&quot;">​</a></h1><p>A. Each licensed surplus lines broker shall report within 30 calendar days to the Commission any change in his residence address, email address, or name.</p><p>B. In addition to the requirements of §§ <a href="/vacode/59.1-69/">59.1-69</a> and <a href="/vacode/59.1-70/">59.1-70</a>, any individual or business entity licensed as a surplus lines broker in the Commonwealth and operating under an assumed or fictitious name shall notify the Commission, at the earlier of the time the application for a surplus lines broker license is filed or within 30 calendar days from the date the assumed or fictitious name is adopted, setting forth the name under which the surplus lines broker intends to operate in Virginia. The Commission shall also be notified within 30 calendar days from the date of cessation of the use of such assumed or fictitious name.</p><p>C. Each licensed surplus lines broker convicted of a felony shall report within 30 calendar days to the Commission the facts and circumstances regarding the criminal conviction.</p><p>D. Each licensed surplus lines broker shall report to the Commission within 30 calendar days of the final disposition of the matter any administrative action taken against him in another jurisdiction or by another governmental agency in the Commonwealth. Such report shall include a copy of the order, consent to order or other relevant legal documents.</p><p>E. Any licensed resident surplus lines broker who has moved his residence from the Commonwealth shall have all licenses immediately terminated by the Commission, whether or not the surplus lines broker has notified the Commission of such move. Nothing shall prohibit such surplus lines broker from applying for a license as a nonresident surplus lines broker.</p><p>F. The license authority of any business entity licensed as a surplus lines broker shall terminate immediately if the sole licensed responsible producer designated pursuant to subdivision F 2 of § <a href="/vacode/38.2-1857.2/">38.2-1857.2</a> for the business entity&#39;s compliance with the insurance laws, rules and regulations of the Commonwealth is removed for any reason, and a new responsible producer has not been designated and the Commission notified within 30 calendar days of such removal and of the newly designated responsible producer.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0213" target="_blank" rel="noreferrer">213</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0498" target="_blank" rel="noreferrer">498</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0675" target="_blank" rel="noreferrer">675</a>.</p>',8),s=[r];function a(l,h,d,c,m,p){return i(),o("div",null,s)}const g=e(n,[["render",a]]);export{f as __pageData,g as default};
