import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 38.2-1871 Licensees not subject to the continuing education requirements of this article.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1871.md","filePath":"vacode/38.2-1871.md"}'),a={name:"vacode/38.2-1871.md"},r=i('<h1 id="§-38-2-1871-licensees-not-subject-to-the-continuing-education-requirements-of-this-article" tabindex="-1">§ 38.2-1871 Licensees not subject to the continuing education requirements of this article. <a class="header-anchor" href="#§-38-2-1871-licensees-not-subject-to-the-continuing-education-requirements-of-this-article" aria-label="Permalink to &quot;§ 38.2-1871 Licensees not subject to the continuing education requirements of this article.&quot;">​</a></h1><p>A. A resident or nonresident agent or public adjuster who has been issued a license during the last 13 months of the two-year period set forth in subsection B of § <a href="/vacode/38.2-1868.1/">38.2-1868.1</a> and in §§ <a href="/vacode/38.2-1825.1/">38.2-1825.1</a>, <a href="/vacode/38.2-1840/">38.2-1840</a>, and <a href="/vacode/38.2-1845.8/">38.2-1845.8</a> shall be exempt from fulfilling the continuing education course requirements set forth in this article for that license for that biennium.</p><p>B. The following licensees are not subject to the continuing education course requirements set forth in this article:</p><ol><li><p>Life and health insurance consultants who are also licensed both as life and annuities insurance agents and as health agents and who satisfy the continuing education course requirements needed for continuation of their life and annuities and health agent licenses;</p></li><li><p>Property and casualty insurance consultants who are also licensed as property and casualty agents and who satisfy the continuing education course requirements needed for continuation of their property and casualty agent license;</p></li><li><p>Nonresident agents who have met the continuing education requirements of their home state and whose home state gives credit to residents of the Commonwealth on the same basis;</p></li><li><p>Nonresident public adjusters who have met the continuing education requirements of their home state and whose home state gives credit to residents of the Commonwealth on the same basis; and</p></li><li><p>Agents who have applied for and received a permanent exemption from the continuing education course requirements set forth in this article by December 31, 2018.</p></li></ol><p>1992, c. 570; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0175" target="_blank" rel="noreferrer">175</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0159" target="_blank" rel="noreferrer">159</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0583" target="_blank" rel="noreferrer">583</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0522" target="_blank" rel="noreferrer">522</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0032" target="_blank" rel="noreferrer">32</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0668" target="_blank" rel="noreferrer">668</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0675" target="_blank" rel="noreferrer">675</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0441" target="_blank" rel="noreferrer">441</a>.</p>',5),o=[r];function s(c,l,h,u,d,f){return t(),n("div",null,o)}const m=e(a,[["render",s]]);export{p as __pageData,m as default};
