import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-1888.4 Travel administrators.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1888.4.md","filePath":"vacode/38.2-1888.4.md"}'),i={name:"vacode/38.2-1888.4.md"},n=r('<h1 id="§-38-2-1888-4-travel-administrators" tabindex="-1">§ 38.2-1888.4 Travel administrators. <a class="header-anchor" href="#§-38-2-1888-4-travel-administrators" aria-label="Permalink to &quot;§ 38.2-1888.4 Travel administrators.&quot;">​</a></h1><p>A. Notwithstanding any other provision of this title, no person shall act or represent itself as a travel administrator for travel insurance in the Commonwealth unless that person:</p><ol><li><p>Is a licensed property and casualty insurance agent in the Commonwealth for activities permitted under that agent license; or</p></li><li><p>Holds a valid managing general agent (MGA) license in the Commonwealth.</p></li></ol><p>B. An insurer is responsible for the acts of a travel administrator administering travel insurance underwritten by the insurer and is responsible for ensuring that the travel administrator maintains all books and records relevant to the insurer to be made available by the travel administrator to the Commissioner upon request.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0346" target="_blank" rel="noreferrer">346</a>.</p>',5),o=[n];function s(l,d,c,p,h,m){return t(),a("div",null,o)}const f=e(i,[["render",s]]);export{v as __pageData,f as default};
