import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-1900 Purposes of chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1900.md","filePath":"vacode/38.2-1900.md"}'),i={name:"vacode/38.2-1900.md"},r=a('<h1 id="§-38-2-1900-purposes-of-chapter" tabindex="-1">§ 38.2-1900 Purposes of chapter. <a class="header-anchor" href="#§-38-2-1900-purposes-of-chapter" aria-label="Permalink to &quot;§ 38.2-1900 Purposes of chapter.&quot;">​</a></h1><p>A. This chapter shall be liberally construed to achieve the purposes stated in subsection B of this section.</p><p>B. The purposes of this chapter are to:</p><ol><li><p>Protect policyholders and the public against the adverse effects of excessive, inadequate or unfairly discriminatory rates;</p></li><li><p>Encourage independent action by insurers and reasonable price competition among insurers as the most effective way to produce rates that conform to the standards of subdivision 1;</p></li><li><p>Provide formal regulatory controls for use if independent action and price competition fail;</p></li><li><p>Authorize cooperative action among insurers in the rate making process, and regulate such cooperation in order to prevent practices that tend to create monopoly or to lessen or destroy competition;</p></li><li><p>Provide rates that are responsive to competitive market conditions and improve the availability of insurance in this Commonwealth; and</p></li><li><p>Regulate the business of insurance in a manner that will preclude application of federal antitrust laws.</p></li></ol><p>1973, c. 504, § 38.1-279.29; 1986, c. 562.</p>',5),s=[r];function n(c,p,l,d,h,u){return t(),o("div",null,s)}const m=e(i,[["render",n]]);export{_ as __pageData,m as default};
