import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 38.2-1903.1 Exemptions of large commercial risks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1903.1.md","filePath":"vacode/38.2-1903.1.md"}'),t={name:"vacode/38.2-1903.1.md"},n=r('<h1 id="§-38-2-1903-1-exemptions-of-large-commercial-risks" tabindex="-1">§ 38.2-1903.1 Exemptions of large commercial risks. <a class="header-anchor" href="#§-38-2-1903-1-exemptions-of-large-commercial-risks" aria-label="Permalink to &quot;§ 38.2-1903.1 Exemptions of large commercial risks.&quot;">​</a></h1><p>A. Notwithstanding any other provision of this title, an insurer shall not be required to file with, or to receive approval from, the Commission for policy forms and rates used in the insurance of large commercial risks.</p><p>B. The form approval and rate filing exemption set forth in subsection A shall not apply to rates and forms for writing workers&#39; compensation policies issued to large commercial risks.</p><p>C. A &quot;large commercial risk&quot; is a person or entity that:</p><ol><li>Has a risk manager to negotiate insurance coverage. A risk manager means (i) an employee of the large commercial risk or (ii) a third party consultant retained by the large commercial risk, who provides skilled services in loss prevention, loss reduction, or risk and insurance coverage analysis, and the purchase of insurance, and who possesses at least one of the following credentials:</li></ol><p>a. A bachelor&#39;s or higher degree in risk management issued by an accredited institution of higher education;</p><p>b. A designation as a Chartered Property and Casualty Underwriter (CPCU) issued by the American Institute for CPCU/Insurance Institute of America;</p><p>c. A designation as an Associate in Risk Management (ARM) issued by the American Institute for CPCU/Insurance Institute of America;</p><p>d. A designation as a Certified Risk Manager (CRM) issued by the National Alliance for Insurance Education &amp; Research;</p><p>e. A designation as a Fellow in Risk Management (FRM) issued by the Global Risk Management Institute/Risk &amp; Insurance Management; or</p><p>f. At least five years of experience in one or more of the following areas of commercial property and casualty insurance: (i) risk financing, (ii) claims administration, (iii) loss prevention, or (iv) risk and insurance coverage analysis; and</p><ol start="2"><li>Meets at least two of the following criteria:</li></ol><p>a. Possesses a net worth in excess of $2 million;</p><p>b. Generates annual revenues in excess of $2 million;</p><p>c. Employs more than 10 full-time or full-time equivalent employees per individual insured;</p><p>d. Pays annual aggregate nationwide insurance premiums in excess of $25,000;</p><p>e. Is a not-for-profit organization or public body generating annual budgeted expenditures of at least $5 million; or</p><p>f. Is a municipality with a population in excess of 30,000.</p><p>D. An insurer providing a policy to a large commercial risk must obtain annual, written certification signed by the risk manager and an officer of the corporation from the large commercial risk certifying that the large commercial risk (i) employs the necessary qualified risk manager and stating the basis for the risk manager&#39;s qualifications; (ii) meets two of the other criteria set forth in subdivision C 2; (iii) is aware that the policy being purchased is not subject to initial state regulatory review or approval of rates and forms; (iv) has the necessary expertise to negotiate its own policy language and rates; and (v) agrees to the use of the exempted rates and forms by its insurer or insurers.</p><p>E. The policyholder certification shall be filed with and retained by the insurance company issuing coverage to the large commercial risk.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0548" target="_blank" rel="noreferrer">548</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0437" target="_blank" rel="noreferrer">437</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0251" target="_blank" rel="noreferrer">251</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0618" target="_blank" rel="noreferrer">618</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0636" target="_blank" rel="noreferrer">636</a>.</p>',21),o=[n];function s(l,c,p,m,g,f){return i(),a("div",null,o)}const u=e(t,[["render",s]]);export{h as __pageData,u as default};
