import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-1916.2 Penalties; injunctive relief; restitution.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-1916.2.md","filePath":"vacode/38.2-1916.2.md"}'),o={name:"vacode/38.2-1916.2.md"},n=a('<h1 id="§-38-2-1916-2-penalties-injunctive-relief-restitution" tabindex="-1">§ 38.2-1916.2 Penalties; injunctive relief; restitution. <a class="header-anchor" href="#§-38-2-1916-2-penalties-injunctive-relief-restitution" aria-label="Permalink to &quot;§ 38.2-1916.2 Penalties; injunctive relief; restitution.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-218/" target="_blank" rel="noreferrer">38.2-218</a>, any insurer, rate service organization or other person who knowingly or willfully violates any provision of § <a href="http://law.lis.virginia.gov/vacode/38.2-1916/" target="_blank" rel="noreferrer">38.2-1916</a> shall be punished for each such violation by a penalty of not more than $100,000 and may be subject to suspension or revocation of any license issued by the Commission.</p><p>B. Any person threatened with injury or damage to his business or property by reason of a violation of § <a href="http://law.lis.virginia.gov/vacode/38.2-1916/" target="_blank" rel="noreferrer">38.2-1916</a> may petition the Commission for injunctive relief pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-220/" target="_blank" rel="noreferrer">38.2-220</a>.</p><p>C. The Commission may require an insurer, rate service organization, or other person to make restitution in the amount of the direct actual financial loss, including any costs associated with bringing such a matter before the Commission and reasonable attorney&#39;s fees, to (i) the Commonwealth, a political subdivision thereof, or any public agency injured in its business or property or (ii) any person injured in his business or property by reason of a violation of § <a href="http://law.lis.virginia.gov/vacode/38.2-1916/" target="_blank" rel="noreferrer">38.2-1916</a>. If the Commission finds that the violation is willful or flagrant, it may increase the restitution payment to an amount not in excess of three times the actual damages sustained.</p><p>1990, c. 596.</p>',5),r=[n];function s(l,c,h,u,f,p){return t(),i("div",null,r)}const m=e(o,[["render",s]]);export{v as __pageData,m as default};
