import{_ as t,o,c as a,z as e,a as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-2130 Coverage for volunteer fire department costs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2130.md","filePath":"vacode/38.2-2130.md"}'),i={name:"vacode/38.2-2130.md"},s=e("h1",{id:"§-38-2-2130-coverage-for-volunteer-fire-department-costs",tabindex:"-1"},[r("§ 38.2-2130 Coverage for volunteer fire department costs. "),e("a",{class:"header-anchor",href:"#§-38-2-2130-coverage-for-volunteer-fire-department-costs","aria-label":'Permalink to "§ 38.2-2130 Coverage for volunteer fire department costs."'},"​")],-1),n=e("p",null,"Every insurer writing a fire policy or fire policy in combination with other insurance coverages shall provide coverage for the cost charged by a volunteer fire department that is not fully funded by real estate taxes or other property taxes for service charges where the fire department is called in to save or protect property insured under such policy from a peril insured against. The limit of such coverage shall be no less than $250. Higher coverage limits may be offered by an insurer for an additional premium. Any bill for such service charges shall be sent to the owner of the property for which the services were rendered. The amount billed shall not exceed the limit of such coverage. This coverage shall not apply to service charges made in response to a call outside of the volunteer fire department's fire protection district, city, or municipality pursuant to a contract.",-1),c=e("p",null,[r("2012, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0371",target:"_blank",rel:"noreferrer"},"371"),r(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0561",target:"_blank",rel:"noreferrer"},"561"),r(".")],-1),l=[s,n,c];function d(h,f,p,u,v,g){return o(),a("div",null,l)}const y=t(i,[["render",d]]);export{_ as __pageData,y as default};
