import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 38.2-218 Penalties and restitution payments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-218.md","filePath":"vacode/38.2-218.md"}'),n={name:"vacode/38.2-218.md"},a=o('<h1 id="§-38-2-218-penalties-and-restitution-payments" tabindex="-1">§ 38.2-218 Penalties and restitution payments. <a class="header-anchor" href="#§-38-2-218-penalties-and-restitution-payments" aria-label="Permalink to &quot;§ 38.2-218 Penalties and restitution payments.&quot;">​</a></h1><p>A. Any person who knowingly or willfully violates any provision of this title or any regulation issued pursuant to this title shall be punished for each violation by a penalty of not more than $5,000.</p><p>B. Any person who violates without knowledge or intent any provision of this title or any rule, regulation, or order issued pursuant to this title may be punished for each violation by a penalty of not more than $1,000. For the purpose of this subsection, a series of similar violations resulting from the same act shall be limited to a penalty in the aggregate of not more than $10,000.</p><p>C. Any violation resulting solely from a malfunction of mechanical or electronic equipment shall not be subject to a penalty.</p><p>D. 1. The Commission may require a person to make restitution in the amount of the direct actual financial loss:</p><p>a. For charging a rate in excess of that provided by statute or by the rates filed with the Commission by the insurer;</p><p>b. For charging a premium that is determined by the Commission to be unfairly discriminatory, such restitution being limited to a period of one year from the date of determination;</p><p>c. For failing to pay amounts explicitly required by the terms of the insurance contract where no aspect of the claim is disputed by the insurer; and</p><p>d. For improperly withholding, misappropriating, or converting any money or property received in the course of doing business.</p><ol start="2"><li>The Commission shall have no jurisdiction to adjudicate controversies growing out of this subsection regarding restitution among insurers, insureds, agents, claimants and beneficiaries.</li></ol><p>E. The provisions provided under this section may be imposed in addition to or without imposing any other penalties or actions provided by law.</p><p>Code 1950, § 38-24; 1952, c. 317, § 38.1-40; 1986, c. 562; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0226" target="_blank" rel="noreferrer">226</a>.</p>',12),r=[a];function s(l,p,d,h,c,u){return t(),i("div",null,r)}const f=e(n,[["render",s]]);export{y as __pageData,f as default};
