import{_ as e,o as n,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-2210 Warning concerning cancellation to appear on application for motor vehicle liability insurance; reason for cancellation or nonrenewal required on application.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2210.md","filePath":"vacode/38.2-2210.md"}'),i={name:"vacode/38.2-2210.md"},r=o('<h1 id="§-38-2-2210-warning-concerning-cancellation-to-appear-on-application-for-motor-vehicle-liability-insurance-reason-for-cancellation-or-nonrenewal-required-on-application" tabindex="-1">§ 38.2-2210 Warning concerning cancellation to appear on application for motor vehicle liability insurance; reason for cancellation or nonrenewal required on application. <a class="header-anchor" href="#§-38-2-2210-warning-concerning-cancellation-to-appear-on-application-for-motor-vehicle-liability-insurance-reason-for-cancellation-or-nonrenewal-required-on-application" aria-label="Permalink to &quot;§ 38.2-2210 Warning concerning cancellation to appear on application for motor vehicle liability insurance; reason for cancellation or nonrenewal required on application.&quot;">​</a></h1><p>A. Any application for the original issuance of a policy of insurance covering liability arising out of the ownership, maintenance, or use of any motor vehicle as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-2212/" target="_blank" rel="noreferrer">38.2-2212</a> shall have the following statement printed on or attached to the application, in boldface type: READ YOUR POLICY. THE POLICY OF INSURANCE FOR WHICH THIS APPLICATION IS BEING MADE, IF ISSUED, MAY BE CANCELLED WITHOUT CAUSE AT THE OPTION OF THE INSURER AT ANY TIME IN THE FIRST 60 DAYS DURING WHICH IT IS IN EFFECT AND AT ANY TIME THEREAFTER FOR REASONS STATED IN THE POLICY.</p><p>B. Any application for the original issuance of a policy of insurance covering liability arising out of the ownership, maintenance, or use of any motor vehicle defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-2212/" target="_blank" rel="noreferrer">38.2-2212</a> that requires the insured to disclose information as to any previous cancellation or refusal to renew shall also permit the insured to offer or provide a full explanation of the reason for the cancellation or refusal to renew.</p><p>C. The notice required by this section shall be given by the insurer to any applicant within ten days of the application in the event the applicant is not provided a written copy of the application and the coverage has been bound by such insurer.</p><p>D. This section shall not apply to the renewal of any policy of insurance.</p><p>1966, c. 523, § 38.1-381.3; 1986, c. 562; 1988, cc. 655, 665; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0558" target="_blank" rel="noreferrer">558</a>.</p>',6),t=[r];function l(c,p,s,h,f,d){return n(),a("div",null,t)}const g=e(i,[["render",l]]);export{_ as __pageData,g as default};
