import{_ as e,o as i,c as o,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 38.2-2213 Discrimination in issuance of motor vehicle insurance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2213.md","filePath":"vacode/38.2-2213.md"}'),a={name:"vacode/38.2-2213.md"},n=r('<h1 id="§-38-2-2213-discrimination-in-issuance-of-motor-vehicle-insurance" tabindex="-1">§ 38.2-2213 Discrimination in issuance of motor vehicle insurance. <a class="header-anchor" href="#§-38-2-2213-discrimination-in-issuance-of-motor-vehicle-insurance" aria-label="Permalink to &quot;§ 38.2-2213 Discrimination in issuance of motor vehicle insurance.&quot;">​</a></h1><p>No insurer or agent shall refuse to issue a motor vehicle insurance policy as defined in § <a href="/vacode/38.2-2212/">38.2-2212</a> solely because of any one or more of the following factors: the age, sex, residence, race, color, creed, national origin, ancestry, marital status, sexual orientation, gender identity, status of a person as a foster care provider or a person in foster care, or lawful occupation, including the military service, of the person seeking the coverage. Nothing in this section prohibits any insurer from limiting the issuance of motor vehicle insurance policies to those who are residents of this Commonwealth nor does this section prohibit any insurer from limiting the issuance of motor vehicle insurance policies only to persons engaging in or who have engaged in a particular profession or occupation, or who are members of a particular religious sect. Nothing in this section prohibits any insurer from setting rates in accordance with relevant actuarial data.</p><p>1976, c. 495, § 38.1-381.6; 1977, c. 181; 1983, c. 61; 1986, c. 562; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0334" target="_blank" rel="noreferrer">334</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1137" target="_blank" rel="noreferrer">1137</a>.</p>',3),t=[n];function s(c,l,h,u,f,d){return i(),o("div",null,t)}const _=e(a,[["render",s]]);export{p as __pageData,_ as default};
