import{_ as e,o as r,c as o,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-2217.1 Insurers required to renew motor vehicle liability coverage for vanpools; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2217.1.md","filePath":"vacode/38.2-2217.1.md"}'),t={name:"vacode/38.2-2217.1.md"},a=i('<h1 id="§-38-2-2217-1-insurers-required-to-renew-motor-vehicle-liability-coverage-for-vanpools-exceptions" tabindex="-1">§ 38.2-2217.1 Insurers required to renew motor vehicle liability coverage for vanpools; exceptions. <a class="header-anchor" href="#§-38-2-2217-1-insurers-required-to-renew-motor-vehicle-liability-coverage-for-vanpools-exceptions" aria-label="Permalink to &quot;§ 38.2-2217.1 Insurers required to renew motor vehicle liability coverage for vanpools; exceptions.&quot;">​</a></h1><p>A. As used in this section, &quot;vanpooling&quot; means the type of joint arrangement described in subdivision 5 of § <a href="http://law.lis.virginia.gov/vacode/46.2-2000.1/" target="_blank" rel="noreferrer">46.2-2000.1</a> and § <a href="http://law.lis.virginia.gov/vacode/46.2-1400/" target="_blank" rel="noreferrer">46.2-1400</a> where such motor vehicles are used to transport commuters to and from their places of employment on a regular basis. &quot;Motor vehicle&quot; as used in this section shall mean any motor vehicle designed to transport not less than ten nor more than fifteen passengers, including the driver, in fixed seats.</p><p>B. If an insurer as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-2212/" target="_blank" rel="noreferrer">38.2-2212</a> who issues or renews a policy of motor vehicle liability insurance to an insured who intends to use a vehicle for vanpooling which was not so used at the time the policy was issued or last renewed has received by certified mail thirty days&#39; written notice that the insured intends to use the vehicle for vanpooling, the insurer shall not cancel or refuse to renew a policy of liability insurance coverage for such motor vehicle used in vanpooling as defined in subsection A of this section, except for one or both of the following specified reasons:</p><ol><li><p>The named insured fails to discharge when due any payment of the premium for the policy or any installment thereof; or</p></li><li><p>The driving record of the named insured or any regular driver is such that it substantially increases the risk.</p></li></ol><p>C. [Repealed.]</p><p>1986, c. 612, § 38.1-381.11; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0337" target="_blank" rel="noreferrer">337</a>.</p>',6),n=[a];function s(l,c,h,d,p,f){return r(),o("div",null,n)}const g=e(t,[["render",s]]);export{v as __pageData,g as default};
