import{_ as o,o as n,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 38.2-2226 Insurer to give notice to claimant of intention to rely on certain defenses and of execution of nonwaiver of rights agreement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2226.md","filePath":"vacode/38.2-2226.md"}'),a={name:"vacode/38.2-2226.md"},r=e("h1",{id:"§-38-2-2226-insurer-to-give-notice-to-claimant-of-intention-to-rely-on-certain-defenses-and-of-execution-of-nonwaiver-of-rights-agreement",tabindex:"-1"},[t("§ 38.2-2226 Insurer to give notice to claimant of intention to rely on certain defenses and of execution of nonwaiver of rights agreement. "),e("a",{class:"header-anchor",href:"#§-38-2-2226-insurer-to-give-notice-to-claimant-of-intention-to-rely-on-certain-defenses-and-of-execution-of-nonwaiver-of-rights-agreement","aria-label":'Permalink to "§ 38.2-2226 Insurer to give notice to claimant of intention to rely on certain defenses and of execution of nonwaiver of rights agreement."'},"​")],-1),s=e("p",null,"Whenever any insurer on a policy of liability insurance discovers a breach of the terms or conditions of the insurance contract by the insured, the insurer shall notify the claimant or the claimant's counsel of the breach. Notification shall be given within forty-five days after discovery by the insurer of the breach or of the claim, whichever is later. Whenever, on account of such breach, a nonwaiver of rights agreement is executed by the insurer and the insured, or a reservation of rights letter is sent by the insurer to the insured, notice of such action shall be given to the claimant or the claimant's counsel within forty-five days after that agreement is executed or the letter is sent, or after notice of the claim is received, whichever is later. Failure to give the notice within forty-five days will result in a waiver of the defense based on such breach to the extent of the claim by operation of law.",-1),c=e("p",null,"Notwithstanding the provisions of this section, in any claim in which a civil action has been filed by the claimant, the insurer shall give notice of reservation of rights in writing to the claimant, or if the claimant is represented by counsel, to claimant's counsel not less than thirty days prior to the date set for trial of the matter. The court, upon motion of the insurer and for good cause shown, may allow such notice to be given fewer than thirty days prior to the trial date. Failure to give the notice within thirty days of the trial date, or such shorter period as the court may have allowed, shall result in a waiver of the defense based on such breach to the extent of the claim by operation of law.",-1),h=e("p",null,[t("1968, c. 410, § 38.1-389.1; 1986, c. 562; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0377",target:"_blank",rel:"noreferrer"},"377"),t("; 2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0728",target:"_blank",rel:"noreferrer"},"728"),t(".")],-1),l=[r,s,c,h];function f(d,u,v,g,m,y){return n(),i("div",null,l)}const _=o(a,[["render",f]]);export{p as __pageData,_ as default};
