import{_ as t,o,c as a,z as e,a as s}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 38.2-2409 Agreement for joint control of money and assets.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2409.md","filePath":"vacode/38.2-2409.md"}'),r={name:"vacode/38.2-2409.md"},n=e("h1",{id:"§-38-2-2409-agreement-for-joint-control-of-money-and-assets",tabindex:"-1"},[s("§ 38.2-2409 Agreement for joint control of money and assets. "),e("a",{class:"header-anchor",href:"#§-38-2-2409-agreement-for-joint-control-of-money-and-assets","aria-label":'Permalink to "§ 38.2-2409 Agreement for joint control of money and assets."'},"​")],-1),i=e("p",null,"Any person required to execute a bond, undertaking or other obligation may agree with his surety to deposit any or all assets for which he and his surety may be held responsible. The deposit shall be with a bank, savings bank, safe deposit company, or trust company authorized by law to do business as such, or with any other depository approved by the court or a judge of the court, if the deposit is otherwise proper. Assets shall be deposited for safekeeping and held in a manner that prevents the withdrawal of the whole or any part of the deposit without the written consent of the surety, or without an order of a court or a judge, made on any notice to the surety which the court or judge directs. The agreement shall not in any manner release or change the liability of the principal or sureties as established by the terms of the bond.",-1),d=e("p",null,"Code 1950, § 38-345; 1952, c. 317, § 38.1-645; 1986, c. 562.",-1),h=[n,i,d];function c(l,p,f,m,u,y){return o(),a("div",null,h)}const g=t(r,[["render",c]]);export{b as __pageData,g as default};
