import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 38.2-2419 Notation of revocation; indexing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2419.md","filePath":"vacode/38.2-2419.md"}'),a={name:"vacode/38.2-2419.md"},n=r('<h1 id="§-38-2-2419-notation-of-revocation-indexing" tabindex="-1">§ 38.2-2419 Notation of revocation; indexing. <a class="header-anchor" href="#§-38-2-2419-notation-of-revocation-indexing" aria-label="Permalink to &quot;§ 38.2-2419 Notation of revocation; indexing.&quot;">​</a></h1><p>When the power of attorney has been revoked in accordance with § <a href="http://law.lis.virginia.gov/vacode/38.2-2417/" target="_blank" rel="noreferrer">38.2-2417</a>, the clerk in whose office the power of attorney is recorded shall record its revocation in the deed book where the power of attorney is recorded. The revocation shall reference the book and page where the original power of attorney is recorded. The clerk shall index the instrument of revocation both in the name of the fidelity and surety insurer and of its attorney-in-fact.</p><p>Code 1950, § 38-342; 1952, c. 317, § 38.1-656; 1986, c. 562; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0357" target="_blank" rel="noreferrer">357</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0470" target="_blank" rel="noreferrer">470</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0330" target="_blank" rel="noreferrer">330</a>.</p>',3),i=[n];function c(d,l,h,s,f,_){return o(),t("div",null,i)}const v=e(a,[["render",c]]);export{g as __pageData,v as default};
