import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-2529 Unearned premium reserves required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2529.md","filePath":"vacode/38.2-2529.md"}'),s={name:"vacode/38.2-2529.md"},n=t('<h1 id="§-38-2-2529-unearned-premium-reserves-required" tabindex="-1">§ 38.2-2529 Unearned premium reserves required. <a class="header-anchor" href="#§-38-2-2529-unearned-premium-reserves-required" aria-label="Permalink to &quot;§ 38.2-2529 Unearned premium reserves required.&quot;">​</a></h1><p>A. Advance assessments received by mutual assessment property and casualty insurers shall be considered premiums and, except as provided in subsection B of this section, shall be subject to the requirement of an unearned premium reserve computed in accordance with § <a href="http://law.lis.virginia.gov/vacode/38.2-1312/" target="_blank" rel="noreferrer">38.2-1312</a>. The reserves may be reduced for applicable reinsurance in accordance with the provisions of Article 3.1 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-1316.1/" target="_blank" rel="noreferrer">38.2-1316.1</a> et seq.) of Chapter 13 of this title.</p><p>B. The amount each insurer shall maintain in reserves for unearned premium reserves shall be as follows:</p><ol><li><p>For calendar year 1987, at least ten percent of the unearned premium reserve as calculated in subsection A of this section; and</p></li><li><p>For each subsequent year, at least an additional ten percent as calculated in subsection A for that subsequent year in order that the full amount of unearned premium reserves shall be established by December 31, 1996.</p></li></ol><p>1986, c. 562; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0316" target="_blank" rel="noreferrer">316</a>.</p>',5),i=[n];function o(c,l,d,u,p,m){return r(),a("div",null,i)}const f=e(s,[["render",o]]);export{_ as __pageData,f as default};
