import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-2601 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2601.md","filePath":"vacode/38.2-2601.md"}'),o={name:"vacode/38.2-2601.md"},i=r('<h1 id="§-38-2-2601-exemptions" tabindex="-1">§ 38.2-2601 Exemptions. <a class="header-anchor" href="#§-38-2-2601-exemptions" aria-label="Permalink to &quot;§ 38.2-2601 Exemptions.&quot;">​</a></h1><p>This chapter shall not apply to:</p><ol><li><p>Performance guarantees given by either (i) the builder of a home or (ii) the manufacturer, seller, or lessor of the property that is the subject of the contract if no identifiable charge is made for the guarantee.</p></li><li><p>Any service contract, guarantee, or warranty intending to guarantee or warrant the repairs or service of a home appliance, component, part, or system that is issued (i) by a person who has sold, serviced, repaired, or provided replacement of the appliance, component, part, or system at the time of or prior to issuance of the service contract, guarantee or warranty if such person does not engage in the business of a home protection company or (ii) by a home protection company which sells such service contracts, guarantees or warranties in the Commonwealth of Virginia and which has net worth in excess of $100 million.</p></li></ol><p>1981, c. 530, § 38.1-933; 1986, c. 562; 1992, c. 21; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0634" target="_blank" rel="noreferrer">634</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>.</p>',4),n=[i];function s(c,p,h,l,m,d){return t(),a("div",null,n)}const u=e(o,[["render",s]]);export{_ as __pageData,u as default};
