import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-2602 Limited applicability to certain insurers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2602.md","filePath":"vacode/38.2-2602.md"}'),r={name:"vacode/38.2-2602.md"},o=i('<h1 id="§-38-2-2602-limited-applicability-to-certain-insurers" tabindex="-1">§ 38.2-2602 Limited applicability to certain insurers. <a class="header-anchor" href="#§-38-2-2602-limited-applicability-to-certain-insurers" aria-label="Permalink to &quot;§ 38.2-2602 Limited applicability to certain insurers.&quot;">​</a></h1><p>A property and casualty insurer may be licensed to transact home protection insurance as defined in § <a href="/vacode/38.2-129/">38.2-129</a>. An insurer licensed in this Commonwealth to transact the class of insurance defined by § <a href="/vacode/38.2-111/">38.2-111</a> on July 1, 1986, may also transact home protection insurance without additional authority. No other provision of this chapter, except § <a href="/vacode/38.2-2606/">38.2-2606</a> and §§ <a href="/vacode/38.2-2608/">38.2-2608</a> through <a href="/vacode/38.2-2614/">38.2-2614</a>, shall be applicable to the insurers, their businesses, or their home protection contracts.</p><p>1981, c. 530, §§ 38.1-933, 38.1-945; 1986, c. 562; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0634" target="_blank" rel="noreferrer">634</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0727" target="_blank" rel="noreferrer">727</a>.</p>',3),n=[o];function c(s,l,d,p,h,_){return a(),t("div",null,n)}const m=e(r,[["render",c]]);export{f as __pageData,m as default};
