import{_ as n,o,c as t,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 38.2-2605 Expiration and renewal of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2605.md","filePath":"vacode/38.2-2605.md"}'),i={name:"vacode/38.2-2605.md"},r=e("h1",{id:"§-38-2-2605-expiration-and-renewal-of-license",tabindex:"-1"},[a("§ 38.2-2605 Expiration and renewal of license. "),e("a",{class:"header-anchor",href:"#§-38-2-2605-expiration-and-renewal-of-license","aria-label":'Permalink to "§ 38.2-2605 Expiration and renewal of license."'},"​")],-1),s=e("p",null,"Every home protection company licensed under this chapter shall obtain a renewal of its license annually from the Commission. Every license issued under this chapter shall expire at midnight on June 30 immediately following the date of issuance. No renewal license shall be issued unless the home protection company has paid all taxes, fees, assessments and other charges imposed upon it, and has complied with all the other requirements of law. The Commission shall not fail or refuse to renew the license of any home protection company without giving the home protection company ten days' notice of the failure or refusal to renew and providing it an opportunity to be heard and to introduce evidence in its behalf. Any such hearing may be informal, and the required notice may be waived by the Commission and the home protection company.",-1),l=e("p",null,[a("1981, c. 530, § 38.1-935; 1986, c. 562; 2006, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0634",target:"_blank",rel:"noreferrer"},"634"),a("; 2017, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0727",target:"_blank",rel:"noreferrer"},"727"),a(".")],-1),c=[r,s,l];function h(d,p,m,f,u,_){return o(),t("div",null,c)}const v=n(i,[["render",h]]);export{y as __pageData,v as default};
