import{_ as e,o,c as t,z as i,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 38.2-2712 Appeal from decision of inspection service, residual market facility or joint underwriting association.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-2712.md","filePath":"vacode/38.2-2712.md"}'),n={name:"vacode/38.2-2712.md"},r=i("h1",{id:"§-38-2-2712-appeal-from-decision-of-inspection-service-residual-market-facility-or-joint-underwriting-association",tabindex:"-1"},[a("§ 38.2-2712 Appeal from decision of inspection service, residual market facility or joint underwriting association. "),i("a",{class:"header-anchor",href:"#§-38-2-2712-appeal-from-decision-of-inspection-service-residual-market-facility-or-joint-underwriting-association","aria-label":'Permalink to "§ 38.2-2712 Appeal from decision of inspection service, residual market facility or joint underwriting association."'},"​")],-1),s=i("p",null,"Any person aggrieved by any action or decision of an inspection service, the residual market facility, or the joint underwriting association may appeal to the Commission within thirty days from the action or the decision. The Commission shall provide the aggrieved person and the inspection service, the residual market facility, or the joint underwriting association an opportunity to be heard on not less than ten days' written notice. The Commission shall then issue an order (i) approving the action or decision, (ii) disapproving the action or decision, or (iii) directing the inspection service, the residual market facility or the joint underwriting association to reinspect the property, or place the application or cause it to be placed pursuant to its plan of operation, whichever is appropriate.",-1),c=i("p",null,"1968, c. 559, § 38.1-754; 1986, c. 562.",-1),d=[r,s,c];function p(l,h,m,f,u,_){return o(),t("div",null,d)}const g=e(n,[["render",p]]);export{y as __pageData,g as default};
