import{_ as e,o as n,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 38.2-3100.3 Requirements of life insurance or annuity contracts used to fund preneed funeral contracts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3100.3.md","filePath":"vacode/38.2-3100.3.md"}'),a={name:"vacode/38.2-3100.3.md"},t=i('<h1 id="§-38-2-3100-3-requirements-of-life-insurance-or-annuity-contracts-used-to-fund-preneed-funeral-contracts" tabindex="-1">§ 38.2-3100.3 Requirements of life insurance or annuity contracts used to fund preneed funeral contracts. <a class="header-anchor" href="#§-38-2-3100-3-requirements-of-life-insurance-or-annuity-contracts-used-to-fund-preneed-funeral-contracts" aria-label="Permalink to &quot;§ 38.2-3100.3 Requirements of life insurance or annuity contracts used to fund preneed funeral contracts.&quot;">​</a></h1><p>A. For purposes of this section, &quot;preneed funeral contract&quot; means any agreement where payment is made by the insured prior to the receipt of services or supplies contracted for, which evidences arrangements prior to death for (i) the providing of funeral services or (ii) the sale of funeral supplies.</p><p>B. Each individual and group life insurance policy issued or issued for delivery in Virginia, each individual and group annuity contract issued or issued for delivery in Virginia, and each certificate issued in connection with a group life insurance policy or group annuity contract issued or issued for delivery in Virginia shall include a provision specifying the means by which face amount adjustments will be made and benefits payable upon death will be adjusted when such a policy or contract will be used to fund a preneed funeral contract.</p><p>C. Each insurer proposing to issue individual or group life insurance policies or individual or group annuity contracts in Virginia for purposes of funding preneed funeral contracts shall clearly disclose the intended purpose and market for such policies and contracts when submitting the forms with the Commission for approval, in accordance with § <a href="/vacode/38.2-316/">38.2-316</a>.</p><p>2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0653" target="_blank" rel="noreferrer">653</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0018" target="_blank" rel="noreferrer">18</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0641" target="_blank" rel="noreferrer">641</a>.</p>',5),o=[t];function s(c,u,d,l,f,p){return n(),r("div",null,o)}const _=e(a,[["render",s]]);export{g as __pageData,_ as default};
