import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-3115 Interest on life insurance and annuity contract proceeds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3115.md","filePath":"vacode/38.2-3115.md"}'),r={name:"vacode/38.2-3115.md"},i=n('<h1 id="§-38-2-3115-interest-on-life-insurance-and-annuity-contract-proceeds" tabindex="-1">§ 38.2-3115 Interest on life insurance and annuity contract proceeds. <a class="header-anchor" href="#§-38-2-3115-interest-on-life-insurance-and-annuity-contract-proceeds" aria-label="Permalink to &quot;§ 38.2-3115 Interest on life insurance and annuity contract proceeds.&quot;">​</a></h1><p>A. If an action to recover the proceeds due under a life insurance policy or annuity contract results in a judgment against the insurer, interest on the judgment at the legal rate of interest shall be paid from (i) the date of presentation to the insurer of proof of death on a life insurance policy or annuity contract or (ii) the date of maturity of an endowment policy to the date judgment is entered.</p><p>B. If no action is brought, interest upon the principal sum paid to the beneficiary or policyowner shall be computed daily at an annual rate of two and one-half percent or at the annual rate currently paid by the insurer on proceeds left under the interest settlement option, whichever is greater, commencing (i) from the date of death on a life insurance policy or annuity contract claim; (ii) from the date of receipt of a completed claim form on a variable annuity contract claim; or (iii) from the date of maturity of an endowment contract to the date of payment. The interest shall be added to and become a part of the total sum payable.</p><p>C. No insurer shall be required to pay interest computed under this section if the total interest is less than five dollars.</p><p>D. This section shall not apply to (i) credit life insurance for which the premium is paid wholly from funds of the creditor with no specific identifiable charge being made to insureds for the insurance and upon which post-death interest on the indebtedness is waived by the creditor in an amount at least equal to the amount of interest that would otherwise be payable under this section; (ii) credit life insurance payable in whole or in part to a creditor that is an affiliate, as defined in § <a href="http://law.lis.virginia.gov/vacode/13.1-725/" target="_blank" rel="noreferrer">13.1-725</a>, of the insurer and that does not charge interest on the indebtedness from the date of death of the insured; or (iii) policies or contracts issued prior to July 1, 1977, but shall apply to any renewals or reissues of group life insurance policies or contracts occurring after that date.</p><p>1977, c. 264, § 38.1-443.1; 1986, c. 562; 1991, c. 368; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0209" target="_blank" rel="noreferrer">209</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0155" target="_blank" rel="noreferrer">155</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0411" target="_blank" rel="noreferrer">411</a>.</p>',6),o=[i];function s(c,l,d,h,f,p){return t(),a("div",null,o)}const _=e(r,[["render",s]]);export{m as __pageData,_ as default};
