import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 38.2-3117.4 Disclosures for retained asset accounts to beneficiaries.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3117.4.md","filePath":"vacode/38.2-3117.4.md"}'),n={name:"vacode/38.2-3117.4.md"},o=i('<h1 id="§-38-2-3117-4-disclosures-for-retained-asset-accounts-to-beneficiaries" tabindex="-1">§ 38.2-3117.4 Disclosures for retained asset accounts to beneficiaries. <a class="header-anchor" href="#§-38-2-3117-4-disclosures-for-retained-asset-accounts-to-beneficiaries" aria-label="Permalink to &quot;§ 38.2-3117.4 Disclosures for retained asset accounts to beneficiaries.&quot;">​</a></h1><p>The insurer shall provide the following written disclosures to the beneficiary of a policy before the retained asset account is selected, if optional, or established, if not optional:</p><ol><li><p>Payment of the full benefit amount is accomplished by delivery of the draft book or check book;</p></li><li><p>One draft or check may be written to access the entire amount, including interest, of the retained asset account at any time;</p></li><li><p>Whether other available settlement options are preserved until the entire balance is withdrawn or the balance drops below the insurer&#39;s minimum balance requirements;</p></li><li><p>A statement identifying the account as either a checking account or a draft account and an explanation of how the account works;</p></li><li><p>Information about the account services provided and contact information where the beneficiary may request and obtain more details about such services;</p></li><li><p>A description of fees charged, if applicable;</p></li><li><p>The frequency of statements showing the current account balance, the interest credited, drafts or checks written, and any other account activity;</p></li><li><p>The minimum interest rate to be credited to the account and how the actual interest rate will be determined;</p></li><li><p>The interest earned on the account may be taxable;</p></li><li><p>Retained asset account funds held by insurance companies are not insured by the Federal Deposit Insurance Corporation but are guaranteed by the state guaranty association. The beneficiary should be advised to contact the National Organization of Life and Health Insurance Guaranty Associations via the association&#39;s website to learn more about the coverage limitations to the account under a state guaranty association; and</p></li><li><p>A description of the insurer&#39;s policy regarding retained asset accounts that become inactive.</p></li></ol><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0194" target="_blank" rel="noreferrer">194</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0227" target="_blank" rel="noreferrer">227</a>.</p>',4),r=[o];function s(c,l,d,h,u,p){return t(),a("div",null,r)}const m=e(n,[["render",s]]);export{b as __pageData,m as default};
