import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 38.2-3213 Same; exemptions from application of certain sections.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3213.md","filePath":"vacode/38.2-3213.md"}'),t={name:"vacode/38.2-3213.md"},o=r('<h1 id="§-38-2-3213-same-exemptions-from-application-of-certain-sections" tabindex="-1">§ 38.2-3213 Same; exemptions from application of certain sections. <a class="header-anchor" href="#§-38-2-3213-same-exemptions-from-application-of-certain-sections" aria-label="Permalink to &quot;§ 38.2-3213 Same; exemptions from application of certain sections.&quot;">​</a></h1><p>A. Sections <a href="http://law.lis.virginia.gov/vacode/38.2-3202/" target="_blank" rel="noreferrer">38.2-3202</a> through <a href="http://law.lis.virginia.gov/vacode/38.2-3212/" target="_blank" rel="noreferrer">38.2-3212</a> shall not apply to any:</p><ol><li><p>Certificates of fraternal benefit societies;</p></li><li><p>Reinsurance;</p></li><li><p>Group insurance;</p></li><li><p>Pure endowments;</p></li><li><p>Annuities or reversionary annuity contracts;</p></li><li><p>Term policies of uniform amount (i) that provide no guaranteed nonforfeiture or endowment benefits, or renewal thereof; (ii) that are of twenty years or less expiring before age seventy-one; and (iii) for which uniform premiums are payable during the entire term of the policy;</p></li><li><p>Term policies of decreasing amount (i) that provide no guaranteed nonforfeiture or endowment benefits; (ii) on which each adjusted premium calculated as specified in §§ <a href="http://law.lis.virginia.gov/vacode/38.2-3205/" target="_blank" rel="noreferrer">38.2-3205</a> through <a href="http://law.lis.virginia.gov/vacode/38.2-3209/" target="_blank" rel="noreferrer">38.2-3209</a> is less than the adjusted premium calculated on term policies of uniform amount, or renewal thereof; (iii) that provide no guaranteed nonforfeiture or endowment benefits; (iv) that are issued at the same age and for the same initial amounts of insurance and for terms of twenty years or less expiring before age seventy-one; and (v) for which uniform premiums are payable during the entire term of the policy;</p></li><li><p>Policies (i) that provide no guaranteed nonforfeiture or endowment benefits and (ii) for which any cash surrender value or present value of any paid-up nonforfeiture benefit at the beginning of any policy year, calculated as specified in §§ <a href="http://law.lis.virginia.gov/vacode/38.2-3203/" target="_blank" rel="noreferrer">38.2-3203</a> through <a href="http://law.lis.virginia.gov/vacode/38.2-3209/" target="_blank" rel="noreferrer">38.2-3209</a>, does not exceed 2 1/2% of the amount of insurance at the beginning of the same policy year; or</p></li><li><p>Policies delivered outside this Commonwealth through an agent or other representative of the insurer issuing the policy.</p></li></ol><p>B. For purposes of determining the applicability of §§ <a href="http://law.lis.virginia.gov/vacode/38.2-3202/" target="_blank" rel="noreferrer">38.2-3202</a> through <a href="http://law.lis.virginia.gov/vacode/38.2-3216/" target="_blank" rel="noreferrer">38.2-3216</a>, the age at expiry for a joint term life insurance policy shall be the age at expiry of the oldest life.</p><p>Code 1950, § 38-382; 1952, c. 317, § 38.1-467; 1962, c. 562; 1982, c. 228; 1986, c. 562.</p>',5),n=[o];function s(l,p,c,f,h,d){return i(),a("div",null,n)}const g=e(t,[["render",s]]);export{u as __pageData,g as default};
