import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-3324 Standard provisions required; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3324.md","filePath":"vacode/38.2-3324.md"}'),o={name:"vacode/38.2-3324.md"},n=a('<h1 id="§-38-2-3324-standard-provisions-required-exceptions" tabindex="-1">§ 38.2-3324 Standard provisions required; exceptions. <a class="header-anchor" href="#§-38-2-3324-standard-provisions-required-exceptions" aria-label="Permalink to &quot;§ 38.2-3324 Standard provisions required; exceptions.&quot;">​</a></h1><p>A. No group life insurance policy shall be delivered or issued for delivery in this Commonwealth unless it contains in substance the standard provisions prescribed in this article. The standard provisions required for individual life insurance policies shall not apply to group life insurance policies.</p><p>B. If a group life insurance policy is not term insurance, it shall contain a nonforfeiture provision that in the opinion of the Commission is equitable to the insured persons and to the policyholder. This subsection shall not be construed to require that group life insurance policies contain the same nonforfeiture provisions as are required for individual life insurance policies.</p><p>C. The provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-3330/" target="_blank" rel="noreferrer">38.2-3330</a>, subsection A of § <a href="http://law.lis.virginia.gov/vacode/38.2-3331/" target="_blank" rel="noreferrer">38.2-3331</a>, and §§ <a href="http://law.lis.virginia.gov/vacode/38.2-3332/" target="_blank" rel="noreferrer">38.2-3332</a> through <a href="http://law.lis.virginia.gov/vacode/38.2-3334/" target="_blank" rel="noreferrer">38.2-3334</a> shall not apply to policies issued pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-3318.1/" target="_blank" rel="noreferrer">38.2-3318.1</a> B or group life insurance contracts in which the insurable interest is as described in subdivision 3 of subsection B of § <a href="http://law.lis.virginia.gov/vacode/38.2-301/" target="_blank" rel="noreferrer">38.2-301</a>.</p><p>Code 1950, §§ 38-429, 38-431; 1952, c. 317, § 38.1-424; 1960, c. 273; 1968, c. 282; 1970, c. 145; 1986, c. 562; 1993, c. 105; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>.</p>',5),t=[n];function s(l,c,d,p,h,u){return i(),r("div",null,t)}const _=e(o,[["render",s]]);export{v as __pageData,_ as default};
