import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 38.2-3407.11:5 Interhospital transfer for newborn or mother; prior authorization prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3407.11:5.md","filePath":"vacode/38.2-3407.11:5.md"}'),i={name:"vacode/38.2-3407.11:5.md"},a=t('<h1 id="§-38-2-3407-11-5-interhospital-transfer-for-newborn-or-mother-prior-authorization-prohibited" tabindex="-1">§ 38.2-3407.11:5 Interhospital transfer for newborn or mother; prior authorization prohibited. <a class="header-anchor" href="#§-38-2-3407-11-5-interhospital-transfer-for-newborn-or-mother-prior-authorization-prohibited" aria-label="Permalink to &quot;§ 38.2-3407.11:5 Interhospital transfer for newborn or mother; prior authorization prohibited.&quot;">​</a></h1><p>A. Notwithstanding any provision of § <a href="/vacode/38.2-3407.11/">38.2-3407.11</a> or <a href="/vacode/38.2-3419/">38.2-3419</a> or any other section of this title to the contrary, no insurer proposing to issue individual or group accident and sickness insurance policies providing hospital, medical and surgical, or major medical coverage on an expense-incurred basis, corporation providing individual or group accident and sickness subscription contracts, or health maintenance organization providing a health care plan for health care services shall require prior authorization for the interhospital transfer of (i) a newborn infant experiencing a life-threatening emergency condition or (ii) the hospitalized mother of such newborn infant to accompany the infant.</p><p>B. The requirements of this section shall apply to all policies, contracts, and plans delivered, issued for delivery, reissued, or extended in the Commonwealth on and after January 1, 2021, or at any time thereafter when any term of the policy, contract, or plan is changed or any premium adjustment is made thereto.</p><p>C. The provisions of this section shall not apply to short-term travel, accident-only, or limited or specified disease policies, contracts designed for issuance to persons eligible for coverage under Title XVIII of the Social Security Act, known as Medicare, or any other similar coverage under state or federal governmental plans, or short-term nonrenewable policies of not more than six months&#39; duration.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>, § 38.2-3407.11:4.</p>',5),n=[a];function s(c,h,p,l,d,f){return r(),o("div",null,n)}const _=e(i,[["render",s]]);export{u as __pageData,_ as default};
