import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-3407.21 Short-term limited-duration medical plans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3407.21.md","filePath":"vacode/38.2-3407.21.md"}'),a={name:"vacode/38.2-3407.21.md"},o=r('<h1 id="§-38-2-3407-21-short-term-limited-duration-medical-plans" tabindex="-1">§ 38.2-3407.21 Short-term limited-duration medical plans. <a class="header-anchor" href="#§-38-2-3407-21-short-term-limited-duration-medical-plans" aria-label="Permalink to &quot;§ 38.2-3407.21 Short-term limited-duration medical plans.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Carrier&quot; means any entity that is authorized to sell, offer, or provide a short-term limited-duration medical plan.</p><p>&quot;Covered person&quot; means a policyholder, subscriber, enrollee, participant, or other individual who is entitled to health care services provided, arranged for, paid for, or reimbursed pursuant to a short-term limited-duration medical plan.</p><p>&quot;PPACA&quot; has the meaning ascribed thereto in § <a href="/vacode/38.2-3438/">38.2-3438</a>.</p><p>&quot;Short-term limited-duration medical plan&quot; has the same meaning as short-term limited-duration insurance as used in 26 C.F.R. § 54.9801-2, 29 C.F.R. § 2590.701-2 and 45 C.F.R. § 144.103 except as described in subsection B.</p><p>B. No carrier shall issue, deliver, issue for delivery, reissue, or extend in the Commonwealth on and after July 1, 2021, any short-term limited-duration medical plan:</p><ol><li><p>With a duration that exceeds three months;</p></li><li><p>That can be renewed or extended if the renewal or extension would result in such coverage being effective for more than six months, notwithstanding § <a href="/vacode/38.2-3514.2/">38.2-3514.2</a>; or</p></li><li><p>If the issuance, delivery, reissuance, or extension of the short-term limited-duration medical plan would result in a covered person being covered by a short-term limited-duration medical plan for more than six months in any 12-month period.</p></li></ol><p>C. No carrier shall issue a short-term limited-duration medical plan during an annual open enrollment period.</p><p>D. Any certificate delivered in the Commonwealth that is issued under a short-term limited-duration medical plan in any other jurisdiction shall comply with the requirements of this section.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1076" target="_blank" rel="noreferrer">1076</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1077" target="_blank" rel="noreferrer">1077</a>.</p>',11),n=[o];function d(s,l,h,c,m,p){return t(),i("div",null,n)}const _=e(a,[["render",d]]);export{f as __pageData,_ as default};
