import{_ as e,o,c as i,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-3411.3 Coverage for childhood immunizations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3411.3.md","filePath":"vacode/38.2-3411.3.md"}'),r={name:"vacode/38.2-3411.3.md"},n=a('<h1 id="§-38-2-3411-3-coverage-for-childhood-immunizations" tabindex="-1">§ 38.2-3411.3 Coverage for childhood immunizations. <a class="header-anchor" href="#§-38-2-3411-3-coverage-for-childhood-immunizations" aria-label="Permalink to &quot;§ 38.2-3411.3 Coverage for childhood immunizations.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-3419/" target="_blank" rel="noreferrer">38.2-3419</a>, each insurer proposing to issue individual or group accident and sickness insurance policies providing hospital, medical and surgical, or major medical coverage on an expense-incurred basis; each corporation providing individual or group accident and sickness subscription contracts; and each health maintenance organization providing a health care plan for health care services shall provide coverage for all routine and necessary immunizations for newborn children under such policy, contract or plan delivered, issued for delivery or renewed in this Commonwealth on and after July 1, 2000.</p><p>B. The required benefits shall apply to immunizations administered to each newborn child from birth to thirty-six months of age.</p><p>C. For the purpose of this section, &quot;routine and necessary immunizations&quot; means immunizations against diphtheria, pertussis, tetanus, polio, hepatitis B, measles, mumps, rubella, and other such immunizations as may be prescribed by the Commissioner of Health.</p><p>D. The provisions of this section shall not apply to any policy, contract or plan under which the policyholder has elected to obtain coverage for child health supervision services offered and made available under § <a href="http://law.lis.virginia.gov/vacode/38.2-3411.1/" target="_blank" rel="noreferrer">38.2-3411.1</a> or to short-term travel, accident-only, limited or specified disease policies, or contracts designed for issuance to persons eligible for coverage under Title XVIII of the Social Security Act, known as Medicare, or any other similar coverage under state or federal governmental plans, or to short-term nonrenewable policies of not more than six months&#39; duration.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0460" target="_blank" rel="noreferrer">460</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0496" target="_blank" rel="noreferrer">496</a>.</p>',6),t=[n];function s(c,l,d,h,p,m){return o(),i("div",null,t)}const g=e(r,[["render",s]]);export{f as __pageData,g as default};
