import{_ as e,o,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-3418.3 Coverage for hemophilia and congenital bleeding disorders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3418.3.md","filePath":"vacode/38.2-3418.3.md"}'),a={name:"vacode/38.2-3418.3.md"},t=r('<h1 id="§-38-2-3418-3-coverage-for-hemophilia-and-congenital-bleeding-disorders" tabindex="-1">§ 38.2-3418.3 Coverage for hemophilia and congenital bleeding disorders. <a class="header-anchor" href="#§-38-2-3418-3-coverage-for-hemophilia-and-congenital-bleeding-disorders" aria-label="Permalink to &quot;§ 38.2-3418.3 Coverage for hemophilia and congenital bleeding disorders.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-3419/" target="_blank" rel="noreferrer">38.2-3419</a>, each insurer proposing to issue individual or group accident and sickness insurance policies providing hospital, medical and surgical, or major medical coverage on an expense-incurred basis; each corporation providing individual or group accident and sickness subscription contracts; and each health maintenance organization providing a health care plan for health care services shall provide coverage for hemophilia and congenital bleeding disorders under such policy, contract or plan delivered, issued for delivery or renewed in this Commonwealth on and after July 1, 1998.</p><p>B. For the purpose of this section:</p><p>&quot;Blood infusion equipment&quot; includes, but is not limited to, syringes and needles.</p><p>&quot;Blood product&quot; includes, but is not limited to, Factor VII, Factor VIII, Factor IX, and cryoprecipitate.</p><p>&quot;Hemophilia&quot; means a lifelong hereditary bleeding disorder usually affecting males that results in prolonged bleeding primarily into joints and muscles.</p><p>&quot;Home treatment program&quot; means a program where individuals or family members are trained to provide infusion therapy at home in order to achieve optimal health and cost effectiveness.</p><p>&quot;State-approved hemophilia treatment center&quot; means a hospital or clinic which receives federal or state Maternal and Child Health Bureau, and/or Centers for Disease Control funds to conduct comprehensive care for persons with hemophilia and other congenital bleeding disorders.</p><p>C. The benefits to be provided shall include coverage for expenses incurred in connection with the treatment of routine bleeding episodes associated with hemophilia and other congenital bleeding disorders. The benefits to be provided shall include coverage for the purchase of blood products and blood infusion equipment required for home treatment of routine bleeding episodes associated with hemophilia and other congenital bleeding disorders when the home treatment program is under the supervision of the state-approved hemophilia treatment center.</p><p>D. The provisions of this section shall not apply to short-term travel, accident only, limited or specified disease policies, policies or contracts designed for issuance to persons eligible for coverage under Title XVIII of the Social Security Act, known as Medicare, or to any other similar coverage under state or federal governmental plans, or to short-term nonrenewable policies of not more than six months&#39; duration.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0043" target="_blank" rel="noreferrer">43</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0120" target="_blank" rel="noreferrer">120</a>.</p>',11),n=[t];function s(d,l,c,p,h,u){return o(),i("div",null,n)}const f=e(a,[["render",s]]);export{m as __pageData,f as default};
