import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-3418.7 Coverage for PSA testing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3418.7.md","filePath":"vacode/38.2-3418.7.md"}'),r={name:"vacode/38.2-3418.7.md"},t=i('<h1 id="§-38-2-3418-7-coverage-for-psa-testing" tabindex="-1">§ 38.2-3418.7 Coverage for PSA testing. <a class="header-anchor" href="#§-38-2-3418-7-coverage-for-psa-testing" aria-label="Permalink to &quot;§ 38.2-3418.7 Coverage for PSA testing.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-3419/" target="_blank" rel="noreferrer">38.2-3419</a>, each insurer proposing to issue individual or group accident and sickness insurance policies providing hospital, medical and surgical, or major medical coverage on an expense-incurred basis; each corporation providing individual or group accident and sickness subscription contracts; and each health maintenance organization providing a health care plan for health care services shall provide coverage to (i) persons age fifty and over and (ii) persons age forty and over who are at high risk for prostate cancer, according to the most recent published guidelines of the American Cancer Society, for one PSA test in a twelve-month period and digital rectal examinations, all in accordance with American Cancer Society guidelines under any such policy, contract or plan delivered, issued for delivery or renewed in this Commonwealth on and after July 1, 1998.</p><p>B. For the purpose of this section, &quot;PSA testing&quot; means the analysis of a blood sample to determine the level of prostate specific antigen.</p><p>C. The provisions of this section shall not apply to (i) short-term travel, accident only, limited or specified disease policies other than cancer policies, (ii) short-term nonrenewable policies of not more than six months&#39; duration, or (iii) policies or contracts designed for issuance to persons eligible for coverage under Title XVIII of the Social Security Act, known as Medicare, or any other similar coverage under state or federal governmental plans.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0709" target="_blank" rel="noreferrer">709</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0858" target="_blank" rel="noreferrer">858</a>.</p>',5),n=[t];function s(c,l,d,p,h,g){return a(),o("div",null,n)}const u=e(r,[["render",s]]);export{v as __pageData,u as default};
