import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 38.2-3418.9 Minimum hospital stay for hysterectomy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3418.9.md","filePath":"vacode/38.2-3418.9.md"}'),o={name:"vacode/38.2-3418.9.md"},i=r('<h1 id="§-38-2-3418-9-minimum-hospital-stay-for-hysterectomy" tabindex="-1">§ 38.2-3418.9 Minimum hospital stay for hysterectomy. <a class="header-anchor" href="#§-38-2-3418-9-minimum-hospital-stay-for-hysterectomy" aria-label="Permalink to &quot;§ 38.2-3418.9 Minimum hospital stay for hysterectomy.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-3419/" target="_blank" rel="noreferrer">38.2-3419</a>, each insurer proposing to issue an individual or group hospital policy or major medical policy in this Commonwealth, each corporation proposing to issue an individual or group hospital, medical or major medical subscription contract, and each health maintenance organization providing a health care plan for health care shall provide coverage for laparoscopy-assisted vaginal hysterectomy and vaginal hysterectomy as provided in this section.</p><p>B. Such coverage shall include benefits for a minimum stay in the hospital of not less than 23 hours for a laparoscopy-assisted vaginal hysterectomy and 48 hours for a vaginal hysterectomy. Nothing in this subsection shall be construed as requiring the provision of the total hours referenced when the attending physician, in consultation with the patient, determines that a shorter period of hospital stay is appropriate.</p><p>C. The requirements of this section shall apply to all insurance policies, contracts and plans delivered, issued for delivery, reissued, or extended on and after July 1, 1999, or at any time thereafter when any term of the policy, contract or plan is changed or any premium adjustment is made.</p><p>D. This section shall not apply to short-term travel, accident-only, or limited or specified disease policies or contracts, nor to policies or contracts designed for issuance to persons eligible for coverage under Title XVIII of the Social Security Act, known as Medicare, or any other similar coverage under state or federal governmental plans.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0643" target="_blank" rel="noreferrer">643</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0649" target="_blank" rel="noreferrer">649</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0922" target="_blank" rel="noreferrer">922</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0814" target="_blank" rel="noreferrer">814</a>.</p>',6),n=[i];function s(l,c,h,p,d,m){return a(),t("div",null,n)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
